ROW.OnConvertDtl:
//
  //可用对象:eaContext,usrinf,srcdtl(源单据明细),bilhdr(目标单据的单据头)
  //      bildtl(目标单据的明细),dtlrowno(当前的目标明细行号)
  //验证是不是相同的ＫＡ
    var sql= "";
  var firstcorp = flwstads.getStringAt(0,"REFID2");
  var db = eaContext.getEADatabase();  
  var acc = eaContext.getAccid();
  var firstka = db.GetSQL(" select nvl(refid,id) from corp where id=&apos;"+firstcorp+"&apos;");    
  var nextcorp = "";
  var nextka = "";
  for(var r=1;r&lt;flwstads.getRowCount();r++){
  	nextcorp = flwstads.getStringAt(r,"REFID2");  	
  	nextka = db.GetSQL(" select nvl(refid,id) from corp where id=&apos;"+nextcorp+"&apos;");  
  	if(!firstka.equals(nextka)){
  		throw new Exception("【回笼错误！不同的渠道不能生成一张资金回笼单，请确认！】");
  	}
  }
  ////////////////////////////////////////
  //end of func

ROW.OnConvert:
//
  //可用对象:eaContext,usrinf,bilhdr(目标单据的单据头),bildtl(目标单据的明细)
  //      flwstads(用户选择的数据流状态，包含字段：SRCBILTYP, SRCBIL, SRCBILSEQ,VALLEFT,val2Convert, MEMO1,MEMO2,GUID)
  var 合计金额 = 0.0;
  var r = bildtl.getRowCount()-1;
  //throw new Exception(bildtl.GetXml());
  for(var i=r;i&gt;=0;i--)
  {
    var 金额 = 1.0 * bildtl.getStringAt(i,"TAXMNY");
    合计金额 += 金额;
    bildtl.DeleteRow(i);
  }
  bilhdr.setValueAt(0,"REFID1",合计金额);
//添加来源发票号码作备注
  var pub = new JavaPackage("com.xlsgrid.net.pub");
  var c = flwstads.getRowCount();
  var note="";
  for(var i=0;i&lt;c;i++)
        if(flwstads.getStringAt(i,"srcbiltyp")=="FS")
        {
          var sql = "select srcbil from datflw where id in (select datflw from bilhdr where biltyp=&apos;FS&apos; and acc=&apos;"+
             usrinf.getAccid()+"&apos; and bilid=&apos;"+flwstads.getStringAt(i,"srcbil")+"&apos;)";
          var ds = pub.EADbTool.QuerySQL(sql);
          for(var j=0;j&lt;ds.getRowCount();j++)
            note += ds.getStringAt(j,0) + " ";
        }
//          throw new Exception(note);
  if(note.length()&gt;0)
    bilhdr.setValueAt(0,"note","对应单据 "+note);
  //end of func

ROW.OnBeforeGenSta:
//
  //可用对象:eaContext,_EADB,bilhdr,bildtl(单据数据集)
  //如果把bilhdr的BILID或bildtl的ITMID清空，则跳过该单据或明细

  //end of func

ROW.OnGenStaMemo:
//
  //可用对象:sta(0:单据头;1:明细),ds(单据数据集),row(当前行号);
  //返回MEMO1或MEMO2,如果返回空字符,用默认的说明信息
  //如果把ds的BILID或ITMID清空，则跳过该单据或明细
  if(sta==1)
  {
    var 科目=ds.getStringAt(row,"ITMID").substring(0,4);

    if(科目!="1122")//&&科目!="1133"&&科目!="1151"&&科目!="1111"&&科目!="2121"&&科目!="2131")
      ds.setValueAt(row,"ITMID","");
  }
  return "";
  //end of func

