ROW.OnConvert:
//
  //可用对象:eaContext,usrinf,srchdr,bilhdr(目标单据的单据头),bildtl(目标单据的明细)
  //      flwstads(用户选择的数据流状态，包含字段：SRCBILTYP, SRCBIL, SRCBILSEQ,VALLEFT,val2Convert, MEMO1,MEMO2,GUID)
  	var mny = bilhdr.getStringAt(0,"REFID3");
//  	throw new Exception(mny);
bilhdr.setValueAt(0,"REFID1",1);
  var pub=new JavaPackage("com.xlsgrid.net.pub");
  var db = new pub.EADatabase();
  var itmid = db.GetSQL("select name from v_option where id=&apos;代垫费用科目&apos;");
  var itmnam = db.GetSQL("select name from accitm where id=&apos;"+itmid+"&apos;");
  	var c = bildtl.getRowCount()-1;
	for(var i=c;i&gt;=0;i--){
		bildtl.DeleteRow(i);
	} 

	var i = bildtl.AddNullRow(-1);
	bildtl.setValueAt(i,"ITMID",itmid);
	bildtl.setValueAt(i,"ITMNAM",itmnam);
	bildtl.setValueAt(i,"REFNAM1","从单据：PM~"+srchdr.getStringAt(0,"BILID")+"生成,金额："+bilhdr.getStringAt(0,"REFID3"));
	bildtl.setValueAt(i,"TAXMNY",bilhdr.getStringAt(0,"REFID3"));


	itmid = db.GetSQL("select name from v_option where id=&apos;应付款科目&apos;");
	itmnam = db.GetSQL("select name from accitm where id=&apos;"+itmid+"&apos;");
	var i = bildtl.AddNullRow(i);
	bildtl.setValueAt(i,"ITMID",itmid);
	bildtl.setValueAt(i,"ITMNAM",itmnam);
	bildtl.setValueAt(i,"MNY",bilhdr.getStringAt(0,"REFID3"));	


  //end of func

