ROW.OnConvertDtl:
//
  //可用对象:eaContext,usrinf,srcdtl(源单据明细),bilhdr(目标单据的单据头)
  //      bildtl(目标单据的明细),dtlrowno(当前的目标明细行号)
 	var pubpack = new JavaPackage("com.xlsgrid.net.pub");

	var db =new pubpack.EADatabase();
	var itmid = flwstads.getStringAt(flwstaidx,"REFID3");

	var sql = "select untnum from item where id=&apos;"+itmid+"&apos;";
	var untnum = db.GetSQL(sql);

	bildtl.setValueAt(flwstaidx,"UNTNUM",untnum);

	bilhdr.setValueAt(0,"REFID1",0.17);

  //end of func

ROW.OnConvert:
//
  //可用对象:eaContext,usrinf,bilhdr(目标单据的单据头),bildtl(目标单据的明细)
  //      flwstads(用户选择的数据流状态，包含字段：SRCBILTYP, SRCBIL, SRCBILSEQ,VALLEFT,val2Convert, MEMO1,MEMO2,GUID)
//	throw new Exception(bildtl.getStringAt(0,"NOTAXMNY"));
for(var r=0;r&lt;bildtl.getRowCount();r++){
	var price =1.0* bildtl.getStringAt(r,"PRICE");
	var untnum =1.0* bildtl.getStringAt(r,"UNTNUM");
	bildtl.setValueAt(r,"PRICE",price*untnum);
	bildtl.setValueAt(r,"REFNAM2","");
}
    //end of func

