ROW.OnGenStaMemo:
//
  //可用对象:sta(0:单据头;1:明细),ds(单据数据集),row(当前行号);
  //返回MEMO1或MEMO2,如果返回空字符,用默认的说明信息
  //如果把ds的BILID或ITMID清空，则跳过该单据或明细
	//throw new Exception(MEMO1+","+ds.getStringAt(row,"QTY"));

	if ( sta == "1" ) {// Memo1
		//throw new Exception( ds.GetXml() );
		var qty = 1.0*ds.getStringAt(row,"QTY");
		//var untnum = 1.0*ds.getStringAt(row,"UNTNUM");
		return ""+qty;
	}
  //end of func
