   var sql1 = "select REFID4 from datflw a,bilhdr b where a.flw = &apos;PO2RV&apos; and a.destbiltyp = &apos;RV&apos; and a.destbil = &apos;"+bilid+"&apos; and a.srcbil = b.bilid"; 
