ROW.OnConvertDtl:
//
  //可用对象:eaContext,usrinf,srchdr,srcdtl(源单据明细),bilhdr(目标单据的单据头)
  //      bildtl(目标单据的明细),dtlrowno(当前的目标明细行号)
  //      flwstads(用户选择的数据流状态，包含字段：SRCBILTYP, SRCBIL, SRCBILSEQ,VALLEFT,val2Convert, MEMO1,MEMO2,GUID)
  //      flwstaidx(当前状态行)

  //end of func

ROW.OnConvert:
//
  //可用对象:eaContext,usrinf,srchdr,bilhdr(目标单据的单据头),bildtl(目标单据的明细)
  //      flwstads(用户选择的数据流状态，包含字段：SRCBILTYP, SRCBIL, SRCBILSEQ,VALLEFT,val2Convert, MEMO1,MEMO2,GUID)
            //throw new Exception(flwstads.GetXml());
            var dat=srchdr.getStringAt(0,"DAT");
            bilhdr.setValueAt(0,"DAT",dat);
            bilhdr.setValueAt(0,"subtyp","12");
            var bilid=srchdr.getStringAt(0,"BILID");
            bilhdr.setValueAt(0,"refnam3","RV."+bilid);
            bilhdr.setValueAt(0,"refid1","");
            for(var row=0;row&lt;flwstads.getRowCount();row++) {             

                  bildtl.setValueAt(0,"subtyp","12");
                  if(flwstads.getStringAt(row,"valleft")!=flwstads.getStringAt(row,"val2convert")) {
                        bildtl.setValueAt(row,"qty",1.0*flwstads.getStringAt(row,"valleft")-1.0*flwstads.getStringAt(row,"val2convert"));
                  }
            }
  //end of func

ROW.OnBeforeGenSta:
//
  //可用对象:eaContext,_EADB,bilhdr,bildtl(单据数据集)
  //如果把bilhdr的BILID或bildtl的ITMID清空，则跳过该单据或明细
      //throw new Exception(bilhdr.getStringAt(0,"refid3"));
      if( bilhdr.getStringAt(0,"subtyp")!="PO" ) {
            bilhdr.setValueAt(0,"subtyp","");        
      } else {
            /*
            bilhdr.setValueAt(0,"subtyp","12");
            for(var row=0;row&lt;bildtl.getRowCount();row++) { 
                  //throw new Exception(1.0*bildtl.getStringAt(row,"taxtyp")-1.0*bildtlraw.getStringAt(row,"qty"));
                  if(1.0*bildtl.getStringAt(row,"taxtyp")!=1.0*bildtlraw.getStringAt(row,"qty")) {
                        bildtlraw.setValueAt(row,"QTY",1.0*bildtl.getStringAt(row,"taxtyp")-1.0*bildtlraw.getStringAt(row,"qty"));
                        bildtl.setValueAt(0,"subtyp","12");
                  } else {
                        bildtl.setValueAt(row,"itmid","");
                  }
            }
            */
      }
     // throw new Exception(bildtl.GetXml());  
  //end of func

ROW.OnGenStaMemo:
//
  //可用对象:sta(0:单据头;1:明细),ds(单据数据集),row(当前行号);
  //返回MEMO1或MEMO2,如果返回空字符,用默认的说明信息
  //如果把ds的BILID或ITMID清空，则跳过该单据或明细
  //如果要保存额外信息到  datflwsta 中,请当sta=0时设置ds的字段,后台处理时回自动保存到
  //对应的 datflwsta 字段中:
  //  LOCID  --  REFID1      LOCNAM  --  REFNAM1
  //  CORPID --  REFID2      CORPNAM --  REFNAM2
  //  SUBTYP --  SRCSUBTYP
  if(sta==0) {
        return "";
  } else {
        return ""+bildtl.getStringAt(row,"itmid")+","+bildtl.getStringAt(row,"itmnam")+","+bildtl.getStringAt(row,"ITMSPC")+"批次："+bildtl.getStringAt(row,"itempc");
  }

  //end of func
