  //可用对象:eaContext,usrinf,srchdr,srcdtl(源单据明细),bilhdr(目标单据的单据头)
  //      bildtl(目标单据的明细),dtlrowno(当前的目标明细行号)
  //      flwstads(用户选择的数据流状态，包含字段：SRCBILTYP, SRCBIL, SRCBILSEQ,VALLEFT,val2Convert, MEMO1,MEMO2,GUID)
  //      flwstaidx(当前状态行)

  //end of func

//
  //可用对象:eaContext,usrinf,srchdr,srcdtl(源单据明细),bilhdr(目标单据的单据头)
  //      bildtl(目标单据的明细),dtlrowno(当前的目标明细行号)
  //      flwstads(用户选择的数据流状态，包含字段：SRCBILTYP, SRCBIL, SRCBILSEQ,VALLEFT,val2Convert, MEMO1,MEMO2,GUID)
  //      flwstaidx(当前状态行)
  var pub=new JavaPackage("com.xlsgrid.net.pub");
  var c = bildtl.getRowCount()-1;
  var sql = "select ITMPRI from FINSUM where acc='"+eaContext.getAccid()+"' and ITEM='";
  var db=eaContext.getEADatabase();
  for(var i=c;i>=0;i--)
  {
    var itm = bildtl.getStringAt(i,"ITMID");
    var isql = sql + itm +"'";
    //throw new Exception(isql);
    try
    {
      var price=db.GetSQL(isql);
      bildtl.setValueAt(i,"PRICE",price);
    }
    catch(e)
    {
    }
  }
  //end of func
