ROW.GRDJSDS_VALUE:
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SplitSheet(sheet,&quot;V&quot;,&quot;50%&quot;);
	_this.SetCellText(sheet,1,1,YYYY+&quot;-&quot;+G_LOGDAT.substring(0,4)+&quot;税收年度同比分析表&quot;);
	showChar(1);
}

function init(sheet)
{
	for (var i=2;i&lt;2+12;i++) {
		var mnystr = _this.GetCellText(sheet,4,i);
		if (mnystr != &quot;&quot;) {
			var m = mnystr.split(&quot;_&quot;);
			_this.SetCellText(sheet,4,i,m[0]);
			_this.SetCellText(sheet,5,i,m[1]);
		}
	}
	_this.RefreshFormula();
}

function showChar(sheet) 
{
	var xml = getxml();

	var title = YYYY+&quot;-&quot;+G_LOGDAT.substring(0,4)+&quot;税收年度同比分析表&quot;;
	var xtitle = &quot;行业&quot;;
	var ytitle = &quot;金额&quot;;
	var chart = new SVGChart(&quot;CHART1&quot;,sheet,-1,-1,1,title,xtitle,ytitle,1000,300,xml );
	chart.m_bShowSeralName = 1;

	chart.load();
}

function getxml()
{
	var xml = &quot;&lt;?xml version = &apos;1.0&apos;?&gt;&lt;ROWSET&gt;&quot;;
	for (var r=4;r&lt;=_this.GetMainCellRangeRow2(0);r++) {
		var hy = _this.GetCellText(0,r,1);
		xml += &quot;&lt;ROW num=\&quot;&quot;+(r-3)+&quot;\&quot; name=\&quot;&quot;+hy+&quot;\&quot;&gt;\n&quot;;
		xml += &quot;&lt;增值税&gt;&quot;+_this.GetCellText(0,r,2)+&quot;&lt;/增值税&gt;\n&quot;;
		//xml += &quot;&lt;所得税&gt;&quot;+_this.GetCellText(0,r,3)+&quot;&lt;/所得税&gt;\n&quot;;
		xml += &quot;&lt;/ROW&gt;\n&quot;;
	}
	xml += &quot;&lt;/ROWSET&gt;&quot;;
	return xml;

}


//计算增长率
function cal()
{
	var old = 0.0;
	for(var i = 4;i&lt; _this.GetRowCount(0)-1;i++){
		var cur = 1.0*_this.GetCellText(0,i,2);
		if ( _this.GetCellText(0,i,2)==&quot;&quot;||_this.GetCellText(0,i,2)==&quot;0&quot; ) break;
		if ( old!=0 ) {
			var val = (cur-old)/old;
			_this.SetCellText(0,i,3,convertRound(val,2) );
		}
		old= cur;
	}
}

function convertRound(ADoubleInput,ADotNumber){
    var tmpValue1=1;
    var tmpValue2=1;
    if (ADotNumber&lt;0) return ADoubleInput;
    if (0==ADoubleInput) return Math.round(ADoubleInput);
    for (i=0;i&lt;ADotNumber;i++){
      tmpValue1=tmpValue1*10;
    }
    tmpValue2=ADoubleInput*tmpValue1;
    return Math.round(tmpValue2)/tmpValue1;
  }

//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
  if ( row&gt;=4 && row&gt;=_this.GetMainCellRangeRow1(sheet)&& row&lt;=_this.GetMainCellRangeRow2(sheet) ) {
		var HyName= _this.GetCellText(sheet,row,1);
		window.open(&quot;show.sp?grdid=041&HYCLS=&quot;+HyName+&quot;&YYYY=&quot;+YYYY+&quot;&UNIT=&quot;+UNIT);
	}
}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

//替换SQL参数
function replaceParam(mwobj,request,sql)
{
	var hy = pub.EAFunc.NVL(request.getParameter(&quot;HY&quot;),&quot;&quot;);
	//throw new Exception(hy);
	if (hy != &quot;&quot;) {
		hy = &quot; and c.id in (&quot;+pub.EAFunc.SQLIN(hy)+&quot;)&quot;;
	}
	sql = pub.EAFunc.Replace(sql,&quot;[%HYCLS]&quot;,hy);

	return sql;

}
