//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	if (col == 11) {
		var fromguid = _this.GetCellText(sheet,row,1);
		window.open(&quot;show.sp?grdid=URLBODY&urlguid=&quot;+fromguid+&quot;&SYSURLID=&quot; );
	}
}

//保存单据数据前
function f_dbeditbeforesave()
{
	for(var i=0;i&lt;=_this.GetMainCellRangeRow2(sheet);i++){
		var utxt = _this.GetCellText(sheet,i,2);
		if(utxt==&apos;U&apos;){			
			var param = new Object();
			param.guid = _this.GetCellText(sheet,i,1);
			try {
				var ret = invokeOSFuncExt(&quot;insert&quot;,param);

			}
			catch(e) {
				alert(e);
			}
		}

	}
}


//SelectBox控件修改单元格内容
function _thisOnSelectBoxCellModify(sheet,row,col,oldvalue,newvalue,key,where)
{
	if(col=3){
		var id=newvalue;
		_sql.querytods(&quot;getCompcls&quot;,&quot;ID=&quot;+id);
		var name = _this.XMLDS_GetString(0,&quot;name&quot;);
		var hycode = _this.XMLDS_GetString(0,&quot;refid&quot;);
			_this.SetCellText(sheet,row,col+1,name);
			_this.SetCellText(sheet,row,col+2,hycode);

	}

}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);
function insert()
{
	var db = null;
	var sql = &quot;&quot;;
	var ret = &quot;&quot;;
	try {
		db = new pub.EADatabase();
		sql = &quot;insert into tax_compclass(id,name,hycode,tax,avgsale,envload,se_ybnsr,se_xgm,year,eff) 
		       select id,name,hycode,tax,avgsale,envload,se_ybnsr,se_xgm,year,&apos;0&apos; eff 
		       from tax_compclass where guid=&apos;&quot;+guid+&quot;&apos;&quot;;
		ret = db.ExcecutSQL(sql);

		db.Commit();
		return ret;
	}
	catch(e) {
		if (db != null) db.Rollback();
		return e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}
