ROW.GRDJSDS_VALUE:
var sheet=0;
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	for(var row=5;row&lt;=_this.GetMainCellRangeRow2(sheet);row++)
	{
		_this.SetToBoolBox(sheet,row,1);
	}

	var chkrol = invokeOSFunc(&quot;GXHZTAX.DesktopLayout.checkGenTask&quot;,&quot;usrid=&quot;+G_USRID+&quot;&thisorgid=&quot;+G_ORGID);
	if (chkrol &gt; 0) {
		_this.AddToolbarButton(&quot;udf_genTasks&quot;,&quot;生成核实任务&quot;,&quot;&quot;,&quot;&quot;,0,0,0,100);
	}
}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
  if(row==5 && col==1){
  	for(var r=5;r&lt;=_this.GetMainCellRangeRow2(sheet);r++){
  		_this.SetCellText(sheet,r,1,newvalue);
  	}
  }
}

//生成
function genTasks()
{
  	var xmlstr=getXmlStr(sheet,6,1,_this.GetMainCellRangeRow2(sheet),_this.GetMainCellRangeCol2(sheet));	
  	var usrids=G_USRID; //用户编号
	var accids=G_ACCID; //帐套号
	var orgs=G_ORGID; //组织号
	var syts = G_SYTID; //系统号 
	var usrnams=G_USRNAM ; //用户名称
	var typ = &quot;3&quot;;

	var param = new Object();
	param.usrids=usrids;
	param.accids=accids;
	param.orgs=orgs;
	param.usrnams=usrnams;
	param.syts=syts;
	param.typ=typ;
	param.yymm1=YYMM1;
	param.yymm2=YYMM2;
	param.xmlstr=xmlstr;

	var enddat = window.showModalDialog(&quot;show.sp?grdid=TKParam&typ=&quot;+typ,&quot;&quot;,&quot;dialogHeight=300px;dialogWidth=420px;toolbar=no;menubar=no;scrollbars=no;resizable=no;location=no;status=no&quot;);
	if (enddat != &quot;undefined&quot; && enddat != null) {
		param.enddat = enddat;
		var msg = invokeOSFuncExt(&quot;TAXMON.DesktopLayout.insertTasks&quot;,param);
		alert(msg);
	}

}

function getXmlStr(sheet,row1,col1,row2,col2)
{
	var xmlstr = &quot;&lt;?xml version=&apos;1.0&apos;?&gt;&lt;ROWSET&gt;&quot;;
	var i = 0;
	for (var r=row1;r&lt;=row2;r++) {
		if (_this.GetCellText(sheet,r,col1) == &quot;1&quot;) {
			xmlstr += &quot;&lt;ROW num=\&quot;&quot;+(++i)+&quot;\&quot;&gt;\n&quot;;
			for (var c=col1;c&lt;=col2;c++) {
				var cname = _this.GetColName(sheet,c);
				var str = _this.GetCellText(sheet,r,c);
				xmlstr += &quot;&lt;&quot;+cname+&quot;&gt;&quot;+str+&quot;&lt;/&quot;+cname+&quot;&gt;\n&quot;
			}
			xmlstr += &quot;&lt;/ROW&gt;\n&quot;;
		}
	}
	xmlstr += &quot;&lt;/ROWSET&gt;&quot;;
	return xmlstr;
}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

//替换SQL参数
function replaceParam(mwobj,request,sql)
{
	var gds = request.getParameter(&quot;GDS&quot;);
	if (gds != &quot;&quot;) {
		sql = pub.EAFunc.Replace(sql,&quot;[%GDSSTR]&quot;,&quot;and nvl(a.gds,&apos;&apos;) IN (select name from v_tax_gds where ID like &apos;&quot;+gds+&quot;%&apos;)&quot;);
	}
	else {
		sql = pub.EAFunc.Replace(sql,&quot;[%GDSSTR]&quot;,&quot;&quot;);
	}
	return sql;
}

ROW.DATDSC:
select &apos;&apos; flag,&apos;2011-01&apos;||&apos;~&apos;||&apos;2011-12&apos; YYMM,a.id,a.name,sum(eleqty) eleqty,envload,sum(ens) ens,
       sum(somny) somny,sum(ens-somny) cz,Ytaxman,taxman 
from (
select a.yymm,a.usrid,a.usrnam,sum(a.eleqty) eleqty,nvl(round(sum(a.eleqty/c.envload*avgsale),2),0) ens,c.envload,
   decode(b.Ytaxman,&apos;1&apos;,&apos;是&apos;,&apos;否&apos;) ytaxman,b.taxman,b.id,b.name
from tax_eledata a,tax_company b,tax_compclass c 
where a.usrid=b.ammno and b.Typclsid=c.id
  and nvl(b.towns,&apos; &apos;) IN (select name from V_TAX_TOWNS WHERE ID like &apos;[%TOWNS]%&apos;)
  and c.id=&apos;[%CORPID]&apos; and b.stat != &apos;注销&apos;
  and a.YYMM between &apos;[%YYMM1]&apos; and &apos;[%YYMM2]&apos;
group by a.yymm,a.usrid,a.usrnam,b.id,b.name,b.Ytaxman,b.taxman,c.envload 
) a,(
select a.yymm,a.id,a.name,sum(a.somny) somny,b.ammno
from tax_taxdata a,v_tax_company b,tax_compclass c where a.id=b.id and b.Typclsid=c.id
  and nvl(b.towns,&apos; &apos;) IN (select name from V_TAX_TOWNS WHERE ID like &apos;[%TOWNS]%&apos;)
  and c.id=&apos;[%CORPID]&apos; and b.stat != &apos;注销&apos;
  and a.YYMM between &apos;[%YYMM1]&apos; and &apos;[%YYMM2]&apos;
group by a.yymm,a.id,a.name,b.ammno
  ) b
where a.yymm=b.yymm(+) and a.usrid=b.ammno(+)  
and nvl(somny,0) &lt; ens
AND nvl(somny,0) between decode(&apos;[%STRSALE]&apos;,&apos;&apos;,0,&apos;[%STRSALE]&apos;) and decode(&apos;[%TOSALE]&apos;,&apos;&apos;,10000000000,&apos;[%TOSALE]&apos;) 
group by a.id,a.name,a.envload,Ytaxman,taxman
