ROW.GRDJSDS_VALUE:
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SetToFileSelectBox(&quot;&quot;,sheet,3,3);
	_this.SetToFileSelectBox(&quot;&quot;,sheet,5,3);
	_this.SetToFileSelectBox(&quot;&quot;,sheet,7,3);
	_this.SetToFileSelectBox(&quot;&quot;,sheet,9,3);
	_this.SetToFileSelectBox(&quot;&quot;,sheet,11,3);

	_this.SetToFileSelectBox(&quot;&quot;,sheet,13,3);
	_this.SetToFileSelectBox(&quot;&quot;,sheet,15,3);
	_this.SetToFileSelectBox(&quot;&quot;,sheet,17,3);
	_this.ShowTabBar(1);
}

//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
   	if(id == &quot;导入&quot;) {
		if(!confirm(&quot;确定导入文件？&quot;)) return ;

		var filepath = &quot;&quot;;
		var typ = &quot;&quot;;

		for (var i=3;i&lt;18;i=i+2) {
			if (i == 3) { typ = 1; filepath =_this.GetCellText(sheet,3,3); } 	//平桂电力
			else if (i == 5) { typ = 5; filepath =_this.GetCellText(sheet,5,3); }	//桂源桂东
			else if (i == 7) { typ = 6; filepath =_this.GetCellText(sheet,7,3); }	//富川电力
			else if (i == 9) { typ = 4; filepath =_this.GetCellText(sheet,9,3); }	//钟山电力
			else if (i == 11) { typ = 7; filepath =_this.GetCellText(sheet,11,3); }	//昭平电力
			else if (i == 13) { typ = 8; filepath =_this.GetCellText(sheet,13,3); }	//南方电网
			else if (i == 15) { typ = 9; filepath =_this.GetCellText(sheet,15,3); }	//临江电力
			else if (i == 17) { typ = 10; filepath =_this.GetCellText(sheet,17,3); }	//水管所

			if (filepath != &quot;&quot;) {
				if (typ == 2 || typ == 3) {
					if (typ == 2) _this.SetCellText(sheet,15,2,&quot;正在导入桂源电力数据...&quot;);
					else if (typ == 3) _this.SetCellText(sheet,15,2,&quot;正在导入桂东电力数据...&quot;);
					var filename = upLoadFile2Server(filepath);
					var param = new Object();
					param.typ=typ;
					param.thisorgid = G_ORGID;
					param.filename = &quot;/u/filestore&quot;+filename;
					var msg = invokeOSFuncExt(&quot;impGuiYuan&quot;,param);
					_this.SetCellText(0,15,2,_this.GetCellText(sheet,15,2)+&quot;\n&quot;+msg);
					alert(msg);
				}
				else {		
					if (typ == 1) _this.SetCellText(sheet,19,2,&quot;正在导入平桂电力数据...&quot;);
					else if (typ == 4) _this.SetCellText(sheet,19,2,&quot;正在导入钟山电力数据...&quot;);
					else if (typ == 5) _this.SetCellText(sheet,19,2,&quot;正在导入桂源桂东电力数据...&quot;);
					else if (typ == 6) _this.SetCellText(sheet,19,2,&quot;正在导入富川电力数据...&quot;);
					else if (typ == 7) _this.SetCellText(sheet,19,2,&quot;正在导入昭平电力数据...&quot;);
					else if (typ == 8) _this.SetCellText(sheet,19,2,&quot;正在导入南方电力数据...&quot;);
					else if (typ == 9) _this.SetCellText(sheet,19,2,&quot;正在导入临江电力数据...&quot;);
					else if (typ == 10) _this.SetCellText(sheet,19,2,&quot;正在导入水管所电力数据...&quot;);

					runImpl(filepath,typ);
				}
				_this.SetCellText(sheet,i,3,&quot;&quot;);
			}

		}
	}
}

function runImpl(filepath,typ)
{
	try {
		var tabnam =  runImp(filepath);
		//alert(tabnam);
		//return ;
		if(tabnam != null && tabnam != &quot;&quot;) {
			var param = new Object();
			param.tabnam = tabnam;
			param.typ = typ;
			param.thisorgid = G_ORGID;
			var msg = invokeOSFuncExt(&quot;insert&quot;,param);
			_this.SetCellText(0,19,2,_this.GetCellText(0,19,2)+&quot;\n&quot;+msg);
			alert(msg);			
		}
	}
	catch(e) {
		alert(&quot;导入失败！&quot;+e);
	}
}

