//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
 if ( row&gt;=5 && row&gt;=_this.GetMainCellRangeRow1(sheet)&& row&lt;=_this.GetMainCellRangeRow2(sheet) ) {
  var hycode = _this.GetCellText(sheet,row,2);
  _sql.querytods(&quot;gethy&quot;,&quot;HYCODE=&quot;+hycode);
  var hy = _this.XMLDS_GetString(0,&quot;ID&quot;);
  var yymm1 = YYMM1;
  var yymm2 = YYMM2;
  var hyname = _this.GetCellText(sheet,row,3);
  var s1 = _this.GetCellText(sheet,row,4);
  var s2 = _this.GetCellText(sheet,row,5);
  var swjg=SWJG_DM;
  window.open(&quot;show.sp?grdid=H01DTL&YYMM1=&quot;+YYMM1+&quot;&YYMM2=&quot;+YYMM2+&quot;&HY=&quot;+hy+&quot;&hyname=&quot;+hyname+&quot;&s1=&quot;+s1+&quot;&s2=&quot;+s2+&quot;&swjg=&quot;+swjg);
 }
}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);
var web = new JavaPackage ( &quot;com.xlsgrid.net.web&quot; );

//显示查询参数前预处理
//用于在查询或报表显示查询参数前的预处理。
//可以往sb（StringBuffer）中append HTML内容或额外附近脚本
//可以修改paramDs的内容，决定哪些参数可见或修改默认值
//  ID:编号;  NAME:标题; KEYVAL:关键字; SQLWHE:WHERE条件; DEFVAL:默认值
//  TIP:提示; EDTFLG:是否可修改;  VISFLG:是否可显示; KEYFLG:关键字段(没有作用)
//  DISPORD:参数显示次序号(修改无效); INPCTL:控件类型
function beforeShowParam(request,sb,paramDs,usrinfo)
//var request=javax.servlet.http.HttpServletRequest(); var sb = new java.lang.StringBuffer();var paramDs = new EAXmlDS();var usrinfo = new web.EAUserinfo();
{
	//throw new Exception(paramDs.GetXml());
	var usrinfo = web.EASession.GetLoginInfo(request);
	var usrorg = usrinfo.getusrOrg();
	var usrid = usrinfo.getUsrid();
	var ds = pub.EADbTool.QuerySQL(&quot;select a.*,nvl(b.SCOPE,&apos;NO&apos;) SCOPE from usr a,v_depts b where a.deptid=b.id and a.org=b.org and a.org=&apos;&quot;+usrorg+&quot;&apos; and a.id=&apos;&quot;+usrid+&quot;&apos;&quot;);
	if (ds.getRowCount() &gt; 0) {
		var scope = ds.getStringAt(0,&quot;SCOPE&quot;);
		if (scope != &quot;ALL&quot;) {
			paramDs.setValueAt(2,11,&quot;0&quot;);
		}
	}
}



//替换SQL参数
function replaceParam(mwobj,request,sql)
{
var usrinfo = web.EASession.GetLoginInfo(request);
	var usrorg = usrinfo.getusrOrg();
	var usrid = usrinfo.getUsrid();
	var swjg_dm = request.getParameter(&quot;SWJG_DM&quot;);
	var ds = pub.EADbTool.QuerySQL(&quot;select a.*,nvl(b.SCOPE,&apos;NO&apos;) SCOPE from usr a,v_depts b where a.deptid=b.id and a.org=b.org and a.org=&apos;&quot;+usrorg+&quot;&apos; and a.id=&apos;&quot;+usrid+&quot;&apos;&quot;);
	if (ds.getRowCount() &gt; 0) {
		var scope = ds.getStringAt(0,&quot;SCOPE&quot;);
		if (scope == &quot;ALL&quot;) {
			sql = pub.EAFunc.Replace(sql,&quot;[%SWJGSTR]&quot;,&quot;and b.swjg_dm like &apos;%[%SWJG_DM]%&apos;&quot;);

		}
		else {
			sql = pub.EAFunc.Replace(sql,&quot;[%SWJGSTR]&quot;,&quot;and b.swjg_dm like (select decode(scope,&apos;ALL&apos;,&apos;%&apos;,substr(&apos;[%SYS_DEPTID]&apos;,0,7 )||&apos;%&apos;) from v_depts where id=&apos;[%SYS_DEPTID]&apos;)&quot;);
		}
	}
	sql = pub.EAFunc.Replace(sql,&quot;[%SWJGSTR]&quot;,&quot;and b.swjg_dm like (select decode(scope,&apos;ALL&apos;,&apos;%&apos;,&apos;[%SYS_DEPTID]&apos;) from v_depts where id=&apos;[%SYS_DEPTID]&apos;)&quot;);

	return sql;
}

ROW.DATDSC:
select rownum rn,hycode,hynam,hj1/10000,hj2/10000,&apos;&apos; zje,&apos;&apos; zjbl,zzs1/10000,xfs1/10000,sds1/10000,sdsw1/10000,zzs2/10000,xfs2/10000,sds2/10000,sdsw2/10000 from(
select s1.hycode,s1.hynam,s1.hj1,s2.hj2,s1.zzs1,s1.xfs1,s1.sds1,s1.sdsw1,s2.zzs2,s2.xfs2,s2.sds2,s2.sdsw2 from
(
select c.refid hycode,c.NAME hynam,nvl(cc.hj1,0) hj1,nvl(cc.zzs,0) zzs1,nvl(cc.xfs,0) xfs1,nvl(cc.sds,0) sds1,nvl(cc.sdsw,0) sdsw1 from v_tax_compcls c,(
select typclsid,sum(sehj) hj1,sum(zzs) zzs,sum(xfs) xfs,sum(sds) sds,sum(sdsw) sdsw from(
select b.typclsid,a.nsrsbh,b.name,zzs,xfs,sds,sdsw,sehj
from tax_kpdata a,tax_company b
where a.NSRSBH=b.id(+)
--and b.swjg_dm like &apos;[%SWJG]%&apos;
[%SWJGSTR]
and b.typclsid like &apos;[%HY]%&apos;
and to_char(jkfsrq,&apos;yyyy-mm&apos;)&gt;=&apos;[%YYMM1]&apos;
and to_char(jkfsrq,&apos;yyyy-mm&apos;)&lt;=&apos;[%YYMM2]&apos;
) group by typclsid
) cc where c.ID=cc.typclsid(+)
order by c.id
) s1,
(
select c.refid hycode,c.NAME hynam,nvl(cc.hj2,0) hj2,nvl(cc.zzs,0) zzs2,nvl(cc.xfs,0) xfs2,nvl(cc.sds,0) sds2,nvl(cc.sdsw,0) sdsw2 from v_tax_compcls c,(
select typclsid,sum(sehj) hj2,sum(zzs) zzs,sum(xfs) xfs,sum(sds) sds,sum(sdsw) sdsw from(
select b.typclsid,a.nsrsbh,b.name,zzs,xfs,sds,sdsw,sehj
from tax_kpdata a,tax_company b
where a.NSRSBH=b.id(+)
--and b.swjg_dm like &apos;[%SWJG]%&apos;
[%SWJGSTR]
and b.typclsid like &apos;[%HY]%&apos;
and to_char(jkfsrq,&apos;yyyy-mm&apos;) &gt;= to_char(add_months(to_date(&apos;[%YYMM1]&apos;,&apos;YYYY-MM&apos;),-12),&apos;YYYY-MM&apos;)
and to_char(jkfsrq,&apos;yyyy-mm&apos;) &lt;= to_char(add_months(to_date(&apos;[%YYMM2]&apos;,&apos;YYYY-MM&apos;),-12),&apos;YYYY-MM&apos;)
) group by typclsid
) cc where c.ID=cc.typclsid(+)
) s2
where s1.hycode=s2.hycode(+)
)
