//取消分派
function cancleTrk()
{
	if (!confirm(&quot;取消选中的任务分派，是否确定？&quot;)) return;
	var param = new Object();
	param.xmlstr = getSelXmlStr();
	try {
		var ret = invokeOSFuncExt(&quot;cancle&quot;,param);
		alert(ret);
	}
	catch (e) {
		alert(&quot;取消失败 &quot;+e);
	}
}

//批量分派
function sendTrk()
{
	var url = &quot;show.sp?grdid=TrkSend&flag=1&quot;;
	var ret = window.showModalDialog(url,window,&quot;scroll:no;status:no;dialogWidth:450px;dialoHeight:100px&quot;);
	if (ret != undefined && ret != &quot;&quot;) {
		var param = new Object();
		param.xmlstr = getSelXmlStr();
		param.usrid = G_USRID;
		param.usrnam = G_USRNAM;
		param.tousr = ret.split(&quot;~&quot;)[0];
		param.enddat = ret.split(&quot;~&quot;)[1];
		try {
			var ret = invokeOSFuncExt(&quot;sendTrk&quot;,param);
			alert(ret);
		}
		catch (e) {
			alert(&quot;批量分派失败 &quot;+e);
		}
	}
}


function getSelXmlStr()
{
	var xml = &quot;&lt;?xml version = &apos;1.0&apos;?&gt;&lt;ROWSET&gt;\n&quot;;
	var num = 0;
	for (var r=_this.GetMainCellRangeRow1(sheet);r&lt;=_this.GetMainCellRangeRow2(sheet);r++) {
		//if (_this.GetCellText(0,r,0) == &quot;1&quot;) {
		if (_this.GetRowSelected(0,r) == &quot;1&quot;) {
			num ++;
			xml += &quot;&lt;ROW num=\&quot;&quot;+num+&quot;\&quot;&gt;&lt;GUID&gt;&quot;+_this.GetCellText(0,r,1)+&quot;&lt;/GUID&gt; &lt;/ROW&gt;\n&quot;;
		}
	}
	xml += &quot;&lt;/ROWSET&gt;&quot;;
	return xml;
}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

function getGuShiMenu()
{
	var sql = &quot;select * from V_DEPT where org=&apos;&quot;+thisorgid+&quot;&apos; and id&lt;&gt;&apos;HSG&apos; order by id &quot;;
	var menuxml = &quot;&quot;;
	var ds = pub.EADbTool.QuerySQL(sql);
	for(var r = 0;r &lt; ds.getRowCount();r ++) {
		var id = ds.getStringAt(r,&quot;id&quot;);
		var name = ds.getStringAt(r,&quot;name&quot;);
		menuxml += &quot;&lt;item id=\&quot;&quot;+id+&quot;\&quot; name=\&quot;&quot;+name+&quot;\&quot;/&gt;&quot;;
	}
	return menuxml;
}

function save()
{
	var db = null;
	try {
		var ds = new pub.EAXmlDS(xmlstr);
		db = new pub.EADatabase();
		var sql = &quot;&quot;;
		var ret = 0;
		for (var i=0;i&lt;ds.getRowCount();i++) {
			var flg = ds.getStringAt(i,&quot;SELECTFLAG&quot;);
			var guid = ds.getStringAt(i,&quot;GUID&quot;);
			if (flg == &quot;1&quot;) {
				sql = &quot;update tax_trkhdr set stat=&apos;8&apos;,todept=&apos;&quot;+deptid+&quot;&apos; where guid=&apos;&quot;+guid+&quot;&apos;&quot;;
				ret += db.ExcecutSQL(sql);
			}
		}
		db.Commit();

		return &quot;成功分派 &quot;+ret+&quot; 条任务到股室&quot;;

	}
	catch(e) {
		if (db != null) db.Rollback();
		return e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}

function cancle()
{
	var db = null;
	try {
		db = new pub.EADatabase();
		var sql = &quot;&quot;;
		var ret = 0;
		var ds = new pub.EAXmlDS(xmlstr);
		for (var i=0;i&lt;ds.getRowCount();i++) {
			var guid = ds.getStringAt(i,&quot;GUID&quot;);
			sql = &quot;update tax_trkhdr set stat=&apos;1&apos;,tousr=null,chkdat=null,chkusr=null,chkusrnam=null where guid=&apos;&quot;+guid+&quot;&apos; and stat=&apos;8&apos;&quot;;
  			ret += db.ExcecutSQL(sql);
		}
		db.Commit();

		return &quot;批量取消任务分派成功，记录数&quot;+ret;
	}
	catch(e) {
		if (db != null) db.Rollback();
		throw new Exception(e.toString());
	}	
	finally {
		if (db != null) db.Close();
	}
}

function sendTrk()
{
	var db = null;
	try {
		db = new pub.EADatabase();
		var sql = &quot;&quot;;
		var ret = 0;
		var ds = new pub.EAXmlDS(xmlstr);
		for (var i=0;i&lt;ds.getRowCount();i++) {
			var guid = ds.getStringAt(i,&quot;GUID&quot;);
			var tousr = db.GetSQL(&quot;select name from V_TAX_TAXMAN where id=&apos;&quot;+ tousr+&quot;&apos;&quot;);
			sql = &quot;update tax_trkhdr set stat=&apos;2&apos;,tousr=&apos;&quot;+tousr+&quot;&apos;,enddat=to_date(&apos;&quot;+enddat+&quot;&apos;,&apos;yyyy-mm-dd&apos;),chkusr=&apos;&quot;+usrid+&quot;&apos;,chkusrnam=&apos;&quot;+usrnam+&quot;&apos;,chkdat=sysdate where guid=&apos;&quot;+guid+&quot;&apos;&quot;;
			ret += db.ExcecutSQL(sql);
		}
		db.Commit();

		return &quot;批量任务分派成功，记录数&quot;+ret;
	}
	catch(e) {
		if (db != null) db.Rollback();
		throw new Exception(e.toString());
	}	
	finally {
		if (db != null) db.Close();
	}
}
