ROW.GRDJSDS_VALUE:
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SplitSheet(sheet,&quot;V&quot;,&quot;50%&quot;);
	_this.SetCellText(sheet,1,1,YYYY+&quot;-&quot;+G_LOGDAT.substring(0,4)+&quot;税收年度同比分析表&quot;);
	_this.SetCellText(sheet,3,3,YYYY);
	_this.SetCellText(sheet,3,2,YYYY-1);

	showChar(1);

	var taxtyps = _this.GetCellText(sheet,2,2);
	if (taxtyps == &quot;&quot;) {
		_this.SetCellText(sheet,2,2,&quot;全部&quot;);
	}
	else {
		var arrs = taxtyps.split(&quot;,&quot;);
		taxtyps = &quot;&quot;;
		for (var i=0;i&lt;arrs.length;i++) {
			if (i == 0) taxtyps = &quot;&apos;&quot;+arrs[i]+&quot;&apos;&quot;;
			else taxtyps += &quot;,&apos;&quot;+arrs[i]+&quot;&apos;&quot;;
		}
		_sql.querytods(&quot;ZSXM&quot;,&quot;IDS=&quot;+taxtyps);
		var taxnames = &quot;&quot;;
		for (var i=0;i&lt;_this.XMLDS_GetRowCount();i++) {
			taxnames += _this.XMLDS_GetString(i,&quot;NAME&quot;)+&quot; &quot;;
		}
		_this.SetCellText(sheet,2,2,taxnames);
	}
}

function init(sheet)
{
	for (var i=2;i&lt;2+12;i++) {
		var mnystr = _this.GetCellText(sheet,4,i);
		if (mnystr != &quot;&quot;) {
			var m = mnystr.split(&quot;_&quot;);
			_this.SetCellText(sheet,4,i,m[0]);
			_this.SetCellText(sheet,5,i,m[1]);
		}
	}
	_this.RefreshFormula();
}

function showChar(sheet) 
{
	var xml = getxml();

	var title = YYYY+&quot;-&quot;+G_LOGDAT.substring(0,4)+&quot;税收同比增长分析表&quot;;
	var xtitle = &quot;月份&quot;;
	var ytitle = &quot;金额&quot;;
	var chart = new SVGChart(&quot;CHART1&quot;,sheet,-1,-1,1,title,xtitle,ytitle,1000,300,xml );
	chart.m_bShowSeralName = 1;

	chart.load();
}

function getxml()
{
	var xml = &quot;&lt;?xml version = &apos;1.0&apos;?&gt;&lt;ROWSET&gt;&quot;;
	var lastyear = YYYY-1 + &quot;年&quot;;
	var queryyear = YYYY + &quot;年&quot;;
	for (var r=4;r&lt;=_this.GetMainCellRangeRow2(0);r++) {
		xml += &quot;&lt;ROW num=\&quot;&quot;+(r-3)+&quot;\&quot; name=\&quot;&quot;+_this.GetCellText(0,r,1)+&quot;\&quot;&gt;\n&quot;;
		xml += &quot;&lt;&quot;+lastyear+&quot;&gt;&quot;+_this.GetCellText(0,r,2)+&quot;&lt;/&quot;+lastyear+&quot;&gt;\n&quot;;
		xml += &quot;&lt;&quot;+queryyear+&quot;&gt;&quot;+_this.GetCellText(0,r,3)+&quot;&lt;/&quot;+queryyear+&quot;&gt;\n&quot;;
		xml += &quot;&lt;/ROW&gt;\n&quot;;
	}
	xml += &quot;&lt;/ROWSET&gt;&quot;;

	return xml;

}


//计算增长率
function cal()
{
	var old = 0.0;
	for(var i = 4;i&lt; _this.GetRowCount(0)-1;i++){
		var cur = 1.0*_this.GetCellText(0,i,2);
		if ( _this.GetCellText(0,i,2)==&quot;&quot;||_this.GetCellText(0,i,2)==&quot;0&quot; ) break;
		if ( old!=0 ) {
			var val = (cur-old)/old;
			_this.SetCellText(0,i,3,convertRound(val,2) );
		}
		old= cur;
	}
}

function convertRound(ADoubleInput,ADotNumber){
    var tmpValue1=1;
    var tmpValue2=1;
    if (ADotNumber&lt;0) return ADoubleInput;
    if (0==ADoubleInput) return Math.round(ADoubleInput);
    for (i=0;i&lt;ADotNumber;i++){
      tmpValue1=tmpValue1*10;
    }
    tmpValue2=ADoubleInput*tmpValue1;
    return Math.round(tmpValue2)/tmpValue1;
  }

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

//替换SQL参数
function replaceParam(mwobj,request,sql)
{
	var taxtyps = request.getParameter(&quot;TAXTYP&quot;);
	if (taxtyps != &quot;&quot; && taxtyps != null) {
		var sumcols = pub.EAFunc.Replace(taxtyps,&quot;,&quot;,&quot;+&quot;);
		sql = pub.EAFunc.Replace(sql,&quot;[%TAXTYPSTR]&quot;,sumcols);
	}
	//增值税、消费税、企业所得税、外商企业所得税、车购税、个人所得税、税务收费
	sql = pub.EAFunc.Replace(sql,&quot;[%TAXTYPSTR]&quot;,&quot;ZZS+XFS+SDSN+SDSW+CLGZSSE+GRSDSSE+XZXSF&quot;);

	return sql;
}
