function convertRound(ADoubleInput,ADotNumber){
    var tmpValue1=1;
    var tmpValue2=1;
    if (ADotNumber&lt;0) return ADoubleInput;
    if (0==ADoubleInput) return Math.round(ADoubleInput);
    for (i=0;i&lt;ADotNumber;i++){
      tmpValue1=tmpValue1*10;
    }
    tmpValue2=ADoubleInput*tmpValue1;
    return Math.round(tmpValue2)/tmpValue1;
  }
//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	var dat1 = YYMM1;
	var dat2 = YYMM2;
	var taxman = _this.GetCellText(sheet,row,2);
	var stat = 9;
	var typ = &quot;&quot;;
	var cnt = _this.GetCellText(sheet,row,4);
	var cnt2 = _this.GetCellText(sheet,row,5);
	var cnt3 = _this.GetCellText(sheet,row,6);
	if (col == 4 && cnt &gt; 0) {
		window.open(&quot;show.sp?grdid=TKDTL&DAT1=&quot;+dat1+&quot;&DAT2=&quot;+dat2+&quot;&TAXMAN=&quot;+taxman+&quot;&TYP=&quot;+typ+&quot;&STAT=&quot;+stat);
	}
	if (col == 5 && cnt2 &gt; 0) {
		stat = 3;
		window.open(&quot;show.sp?grdid=TKDTL&DAT1=&quot;+dat1+&quot;&DAT2=&quot;+dat2+&quot;&TAXMAN=&quot;+taxman+&quot;&TYP=&quot;+typ+&quot;&STAT=&quot;+stat);
	}
	if (col == 6 && cnt3 &gt; 0) {
		stat = 2;
		window.open(&quot;show.sp?grdid=TKDTL&DAT1=&quot;+dat1+&quot;&DAT2=&quot;+dat2+&quot;&TAXMAN=&quot;+taxman+&quot;&TYP=&quot;+typ+&quot;&STAT=&quot;+stat);
	}
}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

//替换SQL参数
function replaceParam(mwobj,request,sql)
{
	var taxman = pub.EAFunc.NVL(request.getParameter(&quot;TAXMAN&quot;),&quot;&quot;);
	//throw new Exception(hy);
	if (taxman != &quot;&quot;) {
		taxman = &quot; and tousr in (&quot;+pub.EAFunc.SQLIN(taxman)+&quot;)&quot;;
	}
	sql = pub.EAFunc.Replace(sql,&quot;[%TAXMAN]&quot;,taxman);

	return sql;

}
