//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	var camno = _this.GetCellText(0,5,2);
	if (id == &quot;确定&quot;) {
		if (camno != &quot;&quot;) {
			window.returnValue = save();
			window.close();
		}
		else {
			window.returnValue = &quot;&quot;; 
			alert(&quot;请输入电表号&quot;);
			//window.close();  
		}
	}
}

