function save()
{
	try {
		var ammno = _this.GetCellText(0,5,2);
		var cmname = _this.GetCellText(0,4,2);
		var cmid = _this.GetCellText(0,3,2);
		var ret = invokeOSFunc(&quot;save&quot;,&quot;guid=&quot;+guid+&quot;&cmid=&quot;+cmid+&quot;&cmname=&quot;+name+&quot;&ammno=&quot;+ammno+&quot;&action=&quot;+action);
		alert(ret);
		return ret;
	}
	catch(e) {
		alert(e);
	}
}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

function save()
{
	var db = null;
	try {
		db = new pub.EADatabase();
		var sql = &quot;&quot;;
		var ret = 0;
		if (action == &quot;update&quot;) {
			sql = &quot;update tax_cmpammno set ammno=&apos;&quot;+ammno+&quot;&apos; where guid=&apos;&quot;+guid+&quot;&apos;&quot;;
		}
		else if (action == &quot;add&quot;) {
			sql = &quot;insert into tax_cmpammno(CMID,CMNAM,AMMNO) values(&apos;&quot;+cmid+&quot;&apos;,&apos;&quot;+cmname+&quot;&apos;,&apos;&quot;+ammno+&quot;&apos;)&quot;;
		}
		ret += db.ExcecutSQL(sql);
		db.Commit();

		return &quot;操作成功！&quot;;
	}
	catch(e) {
		if (db != null) db.Rollback();
		throw new Exception(e.toString());
	}	
	finally {
		if (db != null) db.Close();
	}
}
