ROW.GRDJSDS_VALUE:
var sheet=0;
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	for(var row=5;row&lt;=_this.GetMainCellRangeRow2(sheet);row++)
	{
		_this.SetToBoolBox(sheet,row,1);
	}

	var chkrol = invokeOSFunc(&quot;GXHZTAX.DesktopLayout.checkGenTask&quot;,&quot;usrid=&quot;+G_USRID+&quot;&thisorgid=&quot;+G_ORGID);
	if (chkrol &gt; 0) {
		_this.AddToolbarButton(&quot;udf_genTasks&quot;,&quot;生成核实任务&quot;,&quot;&quot;,&quot;&quot;,0,0,0,100);
	}


	var cz = 0;

	//数据差额颜色预警
	for(var i=6;i&lt;=_this.GetMainCellRangeRow2(sheet);i++){
		_this.SetToBoolBox(sheet,i,1);

		cz = _this.GetCellText(sheet,i,6)-_this.GetCellText(sheet,i,7);
		if(cz!=&quot;&quot;){
			//正常，绿色色块			
			if(cz&lt;=50000){
				//_this.SetCellBkColor(sheet,i,10,102,205,0);
				_this.SetCellBkColor(sheet,i,8,0,205,0);
				_this.SetCellText(sheet,i,8,&quot;&quot;);
			}
			//正低风险，黄色色块
			if(cz&gt;50000 && cz&lt;=100000){
				//_this.SetCellBkColor(sheet,i,9,255,215,0);
				_this.SetCellBkColor(sheet,i,8,255,255,0);
				_this.SetCellText(sheet,i,8,&quot;&quot;);
			}
			//中度风险，橙色色块
			if(cz&gt;100000 && cz&lt;=200000){
				//_this.SetCellBkColor(sheet,i,8,255,255,0);
				_this.SetCellBkColor(sheet,i,8,230,165,25);
				_this.SetCellText(sheet,i,8,&quot;&quot;);
			}
			//高度风险，红色色块
			if(cz&gt;200000){
				_this.SetCellBkColor(sheet,i,8,255,0,0);
				_this.SetCellText(sheet,i,8,&quot;&quot;);
			}
		}
	}

}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
  if(row==5 && col==1){
  	for(var r=5;r&lt;=_this.GetMainCellRangeRow2(sheet);r++){
  		_this.SetCellText(sheet,r,1,newvalue);
  	}
  }
}

//生成
function genTasks()
{
  	var xmlstr=getXmlStr(sheet,6,1,_this.GetMainCellRangeRow2(sheet),_this.GetMainCellRangeCol2(sheet));	
  	var usrids=G_USRID; //用户编号
	var accids=G_ACCID; //帐套号
	var orgs=G_ORGID; //组织号
	var syts = G_SYTID; //系统号 
	var usrnams=G_USRNAM ; //用户名称
	var typ = &quot;8&quot;;

	var param = new Object();
	param.usrids=usrids;
	param.accids=accids;
	param.orgs=orgs;
	param.usrnams=usrnams;
	param.syts=syts;
	param.typ=typ;
	param.yymm1=YYMM1;
	param.yymm2=YYMM2;
	param.xmlstr=xmlstr;
	param.swjg_dm = G_DEPTID;
	var enddat = window.showModalDialog(&quot;show.sp?grdid=TKParam&typ=&quot;+typ,&quot;&quot;,&quot;dialogHeight=300px;dialogWidth=420px;toolbar=no;menubar=no;scrollbars=no;resizable=no;location=no;status=no&quot;);
	if (enddat != &quot;undefined&quot; && enddat != null) {
		param.enddat = enddat;
		var msg = invokeOSFuncExt(&quot;TAXMON.DesktopLayout.insertTasks&quot;,param);
		alert(msg);
	}

}

function getXmlStr(sheet,row1,col1,row2,col2)
{
	var xmlstr = &quot;&lt;?xml version=&apos;1.0&apos;?&gt;&lt;ROWSET&gt;&quot;;
	var i = 0;
	for (var r=row1;r&lt;=row2;r++) {
		if (_this.GetCellText(sheet,r,col1) == &quot;1&quot;) {
			xmlstr += &quot;&lt;ROW num=\&quot;&quot;+(++i)+&quot;\&quot;&gt;\n&quot;;
			for (var c=col1;c&lt;=col2;c++) {
				var cname = _this.GetColName(sheet,c);
				var str = _this.GetCellText(sheet,r,c);
				xmlstr += &quot;&lt;&quot;+cname+&quot;&gt;&quot;+str+&quot;&lt;/&quot;+cname+&quot;&gt;\n&quot;
			}
			xmlstr += &quot;&lt;/ROW&gt;\n&quot;;
		}
	}
	xmlstr += &quot;&lt;/ROWSET&gt;&quot;;
	return xmlstr;
}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);
var web = new JavaPackage ( &quot;com.xlsgrid.net.web&quot; );

//显示查询参数前预处理
//用于在查询或报表显示查询参数前的预处理。
//可以往sb（StringBuffer）中append HTML内容或额外附近脚本
//可以修改paramDs的内容，决定哪些参数可见或修改默认值
//  ID:编号;  NAME:标题; KEYVAL:关键字; SQLWHE:WHERE条件; DEFVAL:默认值
//  TIP:提示; EDTFLG:是否可修改;  VISFLG:是否可显示; KEYFLG:关键字段(没有作用)
//  DISPORD:参数显示次序号(修改无效); INPCTL:控件类型
function beforeShowParam(request,sb,paramDs,usrinfo)
//var request=javax.servlet.http.HttpServletRequest(); var sb = new java.lang.StringBuffer();var paramDs = new EAXmlDS();var usrinfo = new web.EAUserinfo();
{
	//throw new Exception(paramDs.GetXml());
	var usrinfo = web.EASession.GetLoginInfo(request);
	var usrorg = usrinfo.getusrOrg();
	var usrid = usrinfo.getUsrid();
	var ds = pub.EADbTool.QuerySQL(&quot;select a.*,nvl(b.SCOPE,&apos;NO&apos;) SCOPE from usr a,v_depts b where a.deptid=b.id and a.org=b.org and a.org=&apos;&quot;+usrorg+&quot;&apos; and a.id=&apos;&quot;+usrid+&quot;&apos;&quot;);
	if (ds.getRowCount() &gt; 0) {
		var scope = ds.getStringAt(0,&quot;SCOPE&quot;);
		if (scope != &quot;ALL&quot;) {
			paramDs.setValueAt(9,11,&quot;0&quot;);
			paramDs.setValueAt(10,11,&quot;0&quot;);
		}
	}
}



//替换SQL参数
function replaceParam(mwobj,request,sql)
{
var usrinfo = web.EASession.GetLoginInfo(request);
	var usrorg = usrinfo.getusrOrg();
	var usrid = usrinfo.getUsrid();
	var swjg_dm = request.getParameter(&quot;SWJG_DM&quot;);
	var ds = pub.EADbTool.QuerySQL(&quot;select a.*,nvl(b.SCOPE,&apos;NO&apos;) SCOPE from usr a,v_depts b where a.deptid=b.id and a.org=b.org and a.org=&apos;&quot;+usrorg+&quot;&apos; and a.id=&apos;&quot;+usrid+&quot;&apos;&quot;);
	if (ds.getRowCount() &gt; 0) {
		var scope = ds.getStringAt(0,&quot;SCOPE&quot;);
		if (scope == &quot;ALL&quot;) {
			sql = pub.EAFunc.Replace(sql,&quot;[%SWJGSTR]&quot;,&quot;and d.swjg_dm like &apos;%[%SWJG_DM]%&apos;&quot;);

		}
		else {
			sql = pub.EAFunc.Replace(sql,&quot;[%SWJGSTR]&quot;,&quot;and d.swjg_dm like (select decode(scope,&apos;ALL&apos;,&apos;%&apos;,substr(&apos;[%SYS_DEPTID]&apos;,0,7 )||&apos;%&apos;) from v_depts where id=&apos;[%SYS_DEPTID]&apos;)&quot;);
		}
	}
	sql = pub.EAFunc.Replace(sql,&quot;[%SWJGSTR]&quot;,&quot;and d.swjg_dm like (select decode(scope,&apos;ALL&apos;,&apos;%&apos;,&apos;[%SYS_DEPTID]&apos;) from v_depts where id=&apos;[%SYS_DEPTID]&apos;)&quot;);

	return sql;
}

ROW.DATDSC:
select flag,rownum rn,id,name,eleqty,elemny,somny,cz,taxman from (
select flag,rownum rn,id,name,eleqty,elemny,somny,elemny-somny cz,taxman from (
select &apos;&apos; flag,aa.cmid id,max(aa.cmnam) name,sum(aa.eleqty) eleqty,sum(aa.elemny) elemny,
nvl(sum(c.somny),1) somny,max(e.name) taxman 
from(
	select b.cmid, sum(a.eleqty) eleqty,sum(a.elemny) elemny,max(b.cmnam) cmnam
	from tax_eledata a,tax_cmpammno b
	where yymm &gt;= &apos;[%YYMM1]&apos; and yymm &lt;= &apos;[%YYMM2]&apos; 
	and a.usrid=b.ammno(+)
	group by b.cmid 
) aa,
(select id,sum(somny) somny from tax_taxdata 
where yymm &gt;= &apos;[%YYMM1]&apos; and yymm &lt;= &apos;[%YYMM2]&apos; 
group by id
) c,tax_company d,v_tax_taxman e
where aa.cmid like &apos;[%CMID]%&apos; 
and aa.eleqty between decode(&apos;[%ETY1]&apos;,&apos;&apos;,0,&apos;[%ETY1]&apos;) 
and decode(&apos;[%ETY2]&apos;,&apos;&apos;,9999999999,&apos;[%ETY2]&apos;) 
and aa.cmid=c.id(+) and c.id=d.id(+) and d.taxman=e.id(+)
and c.somny-aa.elemny&lt;0
and d.typclsid like &apos;[%CORPID]%&apos;
 and d.taxman like &apos;%[%TAXMAN]%&apos;
 [%SWJGSTR]
--and d.swjg_dm like (select decode(scope,&apos;ALL&apos;,&apos;%&apos;,substr(&apos;[%SYS_DEPTID]&apos;,0,7 )||&apos;%&apos;) from v_depts where id=&apos;[%SYS_DEPTID]&apos;)
group by aa.cmid
)
)order by cz desc
