ROW.GRDJSDS_VALUE:
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	for (var r=3;r&lt;=35;r++) {
		_this.SetToBoolBox(sheet,r,1);
	}
}

//执行导入
function doImport()
{
	var param = new Object();
	param.xmlstr = _this.GetXml(0,3,1,13,3);
	try {
		var ret = invokeOSFuncExt(&quot;exceImport&quot;,param);
		alert(ret);
	}
	catch(e) {
		alert(e);
	}
}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

function exceImport()
{
	var db = null;
	var sql = &quot;&quot;;
	var ret = &quot;&quot;;
	try {
		db = new pub.EADatabase();
		var ds = new pub.EAXmlDS(xmlstr);
		for (var i=0;i&lt;ds.getRowCount();i++) {
			var flg = ds.getStringAt(i,&quot;FLAG&quot;);
			var tablename = ds.getStringAt(i,&quot;TABLENAME&quot;);
			if (flg == &quot;1&quot;) {
				var cnt = synOneTable(db,tablename);
				ret += &quot;导入表[&quot;+tablename+&quot;]记录数:&quot;+cnt+&quot;\n&quot;;
			}
		}
		db.Commit();
		return &quot;导入完成\n&quot;+ret;
	}
	catch(e) {
		if (db != null) db.Rollback();
		return e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}

function synOneTable(db,tablename,where) {
	var swhere = &quot;&quot;;
	var sql = &quot;select * from user_tables where table_name=upper(&apos;&quot;+tablename+&quot;&apos;)&quot;;
	if (where != &quot;&quot;) {
		swhere = &quot; where &quot; + where;
	}

	if (db.GetSQLRowCount(sql) &gt; 0) {
		sql = &quot;delete from &quot;+tablename;
		db.ExcecutSQL(sql);
	}
	else {
		sql = &quot;create table &quot;+tablename+&quot; as select * from &quot; + tablename + &quot;@taxdb&quot; + swhere;
		db.ExcecutSQL(sql);
	}
	sql = &quot;insert into &quot;+tablename+&quot; select * from &quot;+tablename+ &quot;@taxdb&quot;;
	var cnt = db.ExcecutSQL(sql);
	return cnt;
}

//后台每天同步
function autoImport()
{
	var tableName = new Array(
		&quot;dj_nsrxx&quot;,		/*纳税人信息表*/
		&quot;dj_nsrxx_kz&quot;,		/*纳税人信息扩展*/
		&quot;dj_sz&quot;,		/*税种*/
		&quot;dj_sz_ls&quot;,		/*税种历史*/
		&quot;dj_nsrzt_bg&quot;,		/*纳税人状态变更*/
		&quot;dm_swjg&quot;,		/*税务机关代码表*/
		&quot;dm_czry&quot;,		/*操作人员代码表*/
		&quot;dm_hy&quot;,		/*行业代码*/
		&quot;dm_hymx&quot;,		/*行业明细代码*/
		&quot;dm_djzclx&quot;,		/*登记注册类型*/
		&quot;dm_nsrzt&quot;,		/*纳税人状态*/
		&quot;dm_fp&quot;,	/*发票代码*/
		&quot;dm_fpzl&quot;,		/*发票种类代码*/
		&quot;dm_pzzl&quot;,		/*凭证种类代码*/
		&quot;dm_zsxm&quot;,		/*征收项目代码*/
		&quot;dm_jdxz&quot;,		/*街道乡镇代码*/
		&quot;v_dm_ysfpbl&quot;,		/*预算分配比例*/
		&quot;rd_nsrzg_lsxx&quot;,	/*纳税人资格认定历史信息*/
		&quot;hd_dsqc&quot;,		/*定税清册*/
		&quot;hd_dsqc_ls&quot;,	/*定税清册历史信息*/
		&quot;FP_DK_FP_QT&quot;,	/*普票代开*/
		&quot;fp_dk_fp&quot;,	/*专票代开*/
		&quot;fp_xs_ls&quot;,	/*发票销售历史信息*/
		&quot;fp_yj&quot;,	/*发票验旧*/
		&quot;fp_pz_ls&quot;,	/*票种登记历史信息*/
		&quot;fp_nsrjc&quot;,	/*发票结存*/
//		&quot;sb_zzs_xgmnsr_2005&quot;,	/*2005版小规模纳税人申报表*/
//		&quot;sb_zzs_2003_ybnsr&quot;,	/*2003班一般纳税人申报表*/
//		&quot;sb_sbxx&quot;,	/*申报信息*/
//		&quot;sb_zsxx&quot;,	/*征收信息*/
		&quot;kj_mdt&quot;,	/*会计免抵调*/
		&quot;kj_srttk&quot;	/*收入提退库*/
	);

	var db = null;
	var sql = &quot;&quot;;

	try {		     
		sql = &quot;insert into tax_imptable_log(id,name,strtime,endtime,cnt) values(&apos;START&apos;,&apos;开始&apos;,sysdate,sysdate,0)&quot;;
		pub.EADbTool.ExcecutSQL(sql);

		for (var i=0;i&lt;tableName.length();i++){
			db = new pub.EADatabase();
			var strtime = db.GetSQL(&quot;select to_char(sysdate,&apos;yyyy-mm-dd hh24:mi:ss&apos;) from dual&quot;);
			var where = &quot;&quot;;
			if(tableName[i] == &quot;sb_zzs_2003_ybnsr&quot;) {
				where = &quot;nsr_swjg_dm like &apos;14511%&apos;&quot;;
			}

			var cnt = synOneTable(db,tableName[i],where);

			//记录日记
			sql = &quot;insert into tax_imptable_log(id,name,strtime,endtime,cnt) 
				values(&apos;&quot;+tableName[i]+&quot;&apos;,&apos;&quot;+tableName[i]+&quot;&apos;,to_date(&apos;&quot;+strtime+&quot;&apos;,&apos;yyyy-mm-dd hh24:mi:ss&apos;),sysdate,&apos;&quot;+cnt+&quot;&apos;)&quot;;
			db.ExcecutSQL(sql);

			db.Commit();
		}

		//税务数据处理
		db = new pub.EADatabase();
		var ret = 0;
		var strtime = db.GetSQL(&quot;select to_char(sysdate,&apos;yyyy-mm-dd hh24:mi:ss&apos;) from dual&quot;);
		sql = &quot;delete from tax_taxdata&quot;;
		db.ExcecutSQL(sql);		
		sql = &quot;insert into tax_taxdata(yymm,id,name,somny,taxmny,sdtaxmny,org)
			select to_char(ssrq,&apos;yyyy-mm&apos;) yymm,nsrsbh,nsrmc,sum(xssr) somny,sum(sbzzse) taxmny,&apos;&apos; adtaxmny,&apos;GXHZ&apos; org
			from v_tax_taxdata_ybnsr
			group by to_char(ssrq,&apos;yyyy-mm&apos;),nsrsbh,nsrmc&quot;;
		ret += db.ExcecutSQL(sql);
		sql = &quot;insert into tax_taxdata(yymm,id,name,somny,taxmny,sdtaxmny,org)
			select to_char(ssrq,&apos;yyyy-mm&apos;) yymm,nsrsbh,nsrmc,sum(xssr) somny,sum(sbzzse) taxmny,&apos;&apos; adtaxmny,&apos;GXHZ&apos; org
			from v_tax_taxdata_xgm
			group by to_char(ssrq,&apos;yyyy-mm&apos;),nsrsbh,nsrmc&quot;;
		ret += db.ExcecutSQL(sql);		
		sql = &quot;insert into tax_imptable_log(id,name,strtime,endtime,cnt) 
				values(&apos;tax_taxdata&apos;,&apos;tax_taxdata&apos;,to_date(&apos;&quot;+strtime+&quot;&apos;,&apos;yyyy-mm-dd hh24:mi:ss&apos;),sysdate,&apos;&quot;+ret+&quot;&apos;)&quot;;
		db.ExcecutSQL(sql);

		//开票税务数据处理
		sql = &quot;insert into tax_kpdata(nsrsbh,sehj,jkfsrq,YSFPBL_DM,zzs,xfs,sds,sdsn,sdsw,grsdsse,clgzsse,xzxsf)
			select * from v_tax_kpdata a where not exists 
			(select 1 from tax_kpdata b where b.nsrsbh=a.nsrsbh and b.jkfsrq=a.jkfsrq ) and a.JKFSRQ&lt;sysdate&quot;;
		ret = db.ExcecutSQL(sql);
		sql = &quot;insert into tax_imptable_log(id,name,strtime,endtime,cnt) 
				values(&apos;tax_kpdata&apos;,&apos;tax_kpdata&apos;,to_date(&apos;&quot;+strtime+&quot;&apos;,&apos;yyyy-mm-dd hh24:mi:ss&apos;),sysdate,&apos;&quot;+ret+&quot;&apos;)&quot;;
		db.ExcecutSQL(sql);


		sql = &quot;insert into tax_imptable_log(id,name,strtime,endtime,cnt) values(&apos;END&apos;,&apos;结束&apos;,sysdate,sysdate,0)&quot;;
		db.ExcecutSQL(sql);

		db.Commit();
	}
	catch(e) {
		if (db != null) db.Rollback();
		var errsql = &quot;insert into tax_imptable_log(id,name,strtime,endtime,cnt,note) values (&apos;error&apos;,&apos;出错&apos;,null,sysdate,0,&apos;&quot;+e.toString()+&quot;&apos;)&quot;;
		pub.EADbTool.ExcecutSQL(errsql);
		return e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}
