ROW.GRDJSDS_VALUE:
var sheet=0;
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SetColVisible(sheet,2,-1);
	_this.SetColVisible(sheet,11,-1);

	for (var r=_this.GetMainCellRangeRow1(sheet)-1;r&lt;=_this.GetMainCellRangeRow2(sheet);r++) {
		_this.SetToBoolBox(sheet,r,0);
	}

	_this.SetCellText(sheet,3,9,getTotal());
}

function getTotal()
{
	var ret = 0;
	for (var i=_this.GetMainCellRangeRow1(sheet);i&lt;=_this.GetMainCellRangeRow2(sheet);i++) {
		if (_this.GetCellText(0,i,3) != &quot;&quot;) ret ++;
	}
	return ret;
}

//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	if ( row&gt;=6 && row&gt;=_this.GetMainCellRangeRow1(sheet)&& row&lt;=_this.GetMainCellRangeRow2(sheet) ) {
		var guid = _this.GetCellText(sheet,row,2);
		var stat = _this.GetCellText(sheet,row,5);
		stat = stat.substring(0,stat.indexOf(&quot;.&quot;));
		window.open(&quot;show.sp?grdid=TK&guid=&quot;+guid+&quot;&processid=&quot;+stat);
	}

}

//取消分派
function cancleTrk()
{
	if (!confirm(&quot;取消选中的任务分派，是否确定？&quot;)) return;
	var param = new Object();
	param.xmlstr = getSelXmlStr();
	try {
		var ret = invokeOSFuncExt(&quot;cancle&quot;,param);
		alert(ret);
	}
	catch (e) {
		alert(&quot;取消失败 &quot;+e);
	}
}

//批量分派
function sendTrk()
{
	var url = &quot;show.sp?grdid=TrkSend&quot;;
	var ret = window.showModalDialog(url,window,&quot;scroll:no;status:no;dialogWidth:450px;dialoHeight:100px&quot;);
	if (ret != undefined && ret != &quot;&quot;) {
		var param = new Object();
		param.xmlstr = getSelXmlStr();
		param.usrid = G_USRID;
		param.usrnam = G_USRNAM;
		param.tousr = ret.split(&quot;~&quot;)[0];
		param.enddat = ret.split(&quot;~&quot;)[1];
		try {
			var ret = invokeOSFuncExt(&quot;sendTrk&quot;,param);
			alert(ret);
		}
		catch (e) {
			alert(&quot;批量分派失败 &quot;+e);
		}
	}
}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if (row == 5 && col == 0) {
		for (var r=_this.GetMainCellRangeRow1(sheet);r&lt;=_this.GetMainCellRangeRow2(sheet);r++) {
			_this.SetCellText(sheet,r,0,newvalue);
		}
	}
}


function getSelXmlStr()
{
	var xml = &quot;&lt;?xml version = &apos;1.0&apos;?&gt;&lt;ROWSET&gt;\n&quot;;
	var num = 0;
	for (var r=_this.GetMainCellRangeRow1(sheet);r&lt;=_this.GetMainCellRangeRow2(sheet);r++) {
		if (_this.GetCellText(0,r,0) == &quot;1&quot;) {
			num ++;
			xml += &quot;&lt;ROW num=\&quot;&quot;+num+&quot;\&quot;&gt;&lt;GUID&gt;&quot;+_this.GetCellText(0,r,2)+&quot;&lt;/GUID&gt; &lt;/ROW&gt;\n&quot;;
		}
	}
	xml += &quot;&lt;/ROWSET&gt;&quot;;
	return xml;
}



//删除任务
function closeTrk()
{
	var ret = confirm(&quot;是否确定要删除选中的任务？&quot;);
	if (ret == true) {
		var param = new Object();
		param.xmlstr = getSelXmlStr();
		param.usrid = G_USRID;
		param.usrnam = G_USRNAM;

		try {
			var ret = invokeOSFuncExt(&quot;closeTrk&quot;,param);
			alert(ret);
			window.location.href = &quot;show.sp?grdid=TKDTL&DAT1=&quot;+DAT1+&quot;&DAT2=&quot;+DAT2+&quot;&TAXMAN=&quot;+TAXMAN+&quot;&TYP=&quot;+TYP+&quot;&STAT=&quot;+STAT+&quot;&BILID=&quot;+BILID+&quot;&DEPT=&quot;+DEPT+&quot;&TRKTYP=&quot;+TRKTYP+&quot;&ELEAREA=&quot;+ELEAREA;
		}
		catch (e) {
			alert(&quot;删除任务失败 &quot;+e);
		}
	}
}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

function cancle()
{
	var db = null;
	try {
		db = new pub.EADatabase();
		var sql = &quot;&quot;;
		var ret = 0;
		var ds = new pub.EAXmlDS(xmlstr);
		for (var i=0;i&lt;ds.getRowCount();i++) {
			var guid = ds.getStringAt(i,&quot;GUID&quot;);
			sql = &quot;update tax_trkhdr set stat=&apos;1&apos;,tousr=null,chkdat=null,chkusr=null,chkusrnam=null where guid=&apos;&quot;+guid+&quot;&apos; and stat=&apos;2&apos;&quot;;
  			ret += db.ExcecutSQL(sql);
		}
		db.Commit();

		return &quot;批量取消任务分派成功，记录数&quot;+ret;
	}
	catch(e) {
		if (db != null) db.Rollback();
		throw new Exception(e.toString());
	}	
	finally {
		if (db != null) db.Close();
	}
}

function sendTrk()
{
	var db = null;
	try {
		db = new pub.EADatabase();
		var sql = &quot;&quot;;
		var ret = 0;
		var ds = new pub.EAXmlDS(xmlstr);
		for (var i=0;i&lt;ds.getRowCount();i++) {
			var guid = ds.getStringAt(i,&quot;GUID&quot;);
			sql = &quot;update tax_trkhdr set stat=&apos;2&apos;,tousr=&apos;&quot;+tousr+&quot;&apos;,enddat=to_date(&apos;&quot;+enddat+&quot;&apos;,&apos;yyyy-mm-dd&apos;),chkusr=&apos;&quot;+usrid+&quot;&apos;,chkusrnam=&apos;&quot;+usrnam+&quot;&apos;,chkdat=sysdate where guid=&apos;&quot;+guid+&quot;&apos; and stat&lt;&gt;&apos;9&apos;&quot;;
			ret += db.ExcecutSQL(sql);
		}
		db.Commit();

		return &quot;批量任务分派成功，记录数&quot;+ret;
	}
	catch(e) {
		if (db != null) db.Rollback();
		throw new Exception(e.toString());
	}	
	finally {
		if (db != null) db.Close();
	}
}


//替换SQL参数
function replaceParam(mwobj,request,sql)
{
	var dept = pub.EAFunc.NVL(request.getParameter(&quot;DEPT&quot;),&quot;&quot;);
	var stat = pub.EAFunc.NVL(request.getParameter(&quot;STAT&quot;),&quot;&quot;);

	if (dept != &quot;&quot;) {
		sql = pub.EAFunc.Replace(sql,&quot;--MORE&quot;,&quot;&quot;);
	}

	//处理传入多个状态的情况
	if (stat.indexOf(&quot;,&quot;) &gt; -1) {
		var stats = pub.EAFunc.SQLIN(stat);
		sql = pub.EAFunc.Replace(sql,&quot;[%STATSTR]&quot;,&quot;and stat in (&quot;+stats+&quot;)&quot;);
	}
	else {
		sql = pub.EAFunc.Replace(sql,&quot;[%STATSTR]&quot;,&quot;and stat like &apos;&quot;+stat+&quot;%&apos;&quot;);
	}

	return sql;

}


function closeTrk()
{
	var db = null;
	try {
		db = new pub.EADatabase();
		var sql = &quot;&quot;;
		var ret = 0;
		var ds = new pub.EAXmlDS(xmlstr);
		for (var i=0;i&lt;ds.getRowCount();i++) {
			var guid = ds.getStringAt(i,&quot;GUID&quot;);
			//sql = &quot;update tax_trkhdr set stat=&apos;0&apos;,chkusr=&apos;&quot;+usrid+&quot;&apos;,chkusrnam=&apos;&quot;+usrnam+&quot;&apos;,chkdat=sysdate where guid=&apos;&quot;+guid+&quot;&apos; and tousr is null and todept is null&quot;;
			sql = &quot;delete from tax_trkhdr where guid=&apos;&quot;+guid+&quot;&apos; and tousr is null and todept is null&quot;;
			ret += db.ExcecutSQL(sql);
		}
		db.Commit();

		return &quot;删除任务成功，记录数&quot;+ret;
	}
	catch(e) {
		if (db != null) db.Rollback();
		throw new Exception(e.toString());
	}	
	finally {
		if (db != null) db.Close();
	}
}
