var sheet=0;

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	for(var row=_this.GetMainCellRangeRow1(sheet);row&lt;=_this.GetMainCellRangeRow2(sheet);row++)
	{
		_this.SetToBoolBox(sheet,row,0);
	}
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this.SELECTFLAG_SINGLEROW);
	_this.SetColVisible(sheet,1,-1);
	_this.AutoFixScreenWidth();
}

var gid = &quot;&quot;;
var cmid = &quot;&quot;;
var name = &quot;&quot;;
var ammno = &quot;&quot;;
var typ = &quot;&quot;;
function getSelectedRow()
{
	for (var r=1;r&lt;_this.GetRowCount(0);r++) {
		if (_this.GetCellText(0,r,0) == &quot;1&quot;) {
			gid = _this.GetCellText(0,r,1);
			cmid = _this.GetCellText(0,r,2);
			name = _this.GetCellText(0,r,3);
			ammno = _this.GetCellText(0,r,4);
			typ = _this.GetCellText(0,r,5);
			return r;
		}
	}
	return -1;
}

//添加电表
function addAMM()
{

	var ret=invokeOSFunc(&quot;qx&quot;,&quot;&quot;);
	if(ret!=&quot;1&quot;){
		alert(&quot;对不起！您没有该操作权限！&quot;);
	}

	var r = getSelectedRow();
	if (r == -1) {
		alert(&quot;请先选中要增加电表的企业！&quot;);
		return;
	}	
	var url = &quot;show.sp?grdid=CMEDITHZ&guid=&quot;+gid+&quot;&cmid=&quot;+cmid+&quot;&name=&quot;+name+&quot;&ammno=&quot;+ammno+&quot;&typ=&quot;+typ+&quot;&act=add&quot;;
	var ret = window.showModalDialog(url,window,&quot;scroll:no;status:no;dialogWidth:500px;dialoHeight:100px&quot;);
	if (ret != undefined && ret != &quot;&quot;) {
		//window.location.reload(true);
	}

} 

//删除
function deleRow()
{
	var ret=invokeOSFunc(&quot;qx&quot;,&quot;&quot;);
	if(ret!=&quot;1&quot;){
		alert(&quot;对不起！您没有该操作权限！&quot;);
	}


	var r = getSelectedRow();
	if (r == -1) {
		alert(&quot;没有选中记录！&quot;);
		return;
	}else if(gid == &quot;&quot;){
	  	alert(&quot;该企业无电表号,无需删除！&quot;);
	  	return;
	}
	if (!confirm(&quot;是否确定删除该记录？&quot;)) return;
	var msg = invokeOSFunc(&quot;del&quot;,&quot;guid=&quot;+_this.GetCellText(0,r,1));
	if (msg &gt; 0) {
		alert(&quot;操作成功！&quot;);
		_this.DeleteRow(0,r);
	}
	else {
		alert(&quot;操作失败！&quot;+msg);
	}
}

//修改
function modify()
{

	var ret=invokeOSFunc(&quot;qx&quot;,&quot;&quot;);
	if(ret!=&quot;1&quot;){
		alert(&quot;对不起！您没有该操作权限！&quot;);
	}

	var row = getSelectedRow();
	if (row != -1 && gid !=&quot;&quot;) {
		//var url = &quot;show.sp?grdid=CMEDITHZ&guid=&quot;+_this.GetCellText(sheet,row,1)+&quot;&act=update&quot;;
		var url = &quot;show.sp?grdid=CMEDITHZ&guid=&quot;+gid+&quot;&cmid=&quot;+cmid+&quot;&name=&quot;+name+&quot;&ammno=&quot;+ammno+&quot;&typ=&quot;+typ+&quot;&act=update&quot;;
		var ret = window.showModalDialog(url,window,&quot;scroll:no;status:no;dialogWidth:500px;dialoHeight:100px&quot;);
		if (ret != undefined && ret != &quot;&quot;) {
			//window.location.reload(true);
		}
	}else{
		alert(&quot;该企业无电表号,请添加电表！&quot;);
	}
}

