	c.name stat,d.name hy,e.name taxman,b.qsrq,b.jzrq
from tax_company a,tax_cmpammno b,V_TAX_NSR_ZT c,
	V_TAX_HYCLASS d,v_Tax_Taxman e,V_TAX_ELECOMPANY f
where a.id=b.cmid(+) and a.stat=c.id(+) and a.typclsid=d.id(+) 
and a.taxman=e.id(+) and b.typ=f.id(+)
