var ZXGFILE0 = &quot;&quot;;
var ym1 = &quot;&quot;;
var ym2 = &quot;&quot;;

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.ShowToolBar(0);
	_this.AutoFixScreenWidth();

	ZXGFILE0 = _this.SaveTempZXGFile(0);

	ym1 = G_LOGDAT.split(&quot;-&quot;)[0] + &quot;-01&quot;;
	ym2 =  G_LOGDAT.substring(0,7);	
	loadData(ym1,ym2);

}


//加载电力数据
function loadData(ym1,ym2)
{
	_this.LoadZXGFile(ZXGFILE0,-1,0);
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this.SELECTFLAG_SINGLEROW);
	_this.SetFixedCol(0,0);
	_this.SetFixedRow(0,3);

	var title = ym1.split(&quot;-&quot;)[0] + &quot;年&quot; + ym1.split(&quot;-&quot;)[1] + &quot;月至&quot; + ym2.split(&quot;-&quot;)[0] + &quot;年&quot; + ym2.split(&quot;-&quot;)[1] + &quot;月&quot;;
	_this.SetCellText(0,0,0,_this.GetCellText(0,0,0)+&quot;（&quot;+title+&quot;）&quot;);
	$(&apos;#chart1&apos;).html(&quot;&lt;div style=\&quot;text-align:center;heigth=100%\&quot;&gt;&lt;br&gt;&lt;br&gt;正在生成，请稍候...&lt;/div&gt;&quot;);
	$(&apos;#chart2&apos;).html(&quot;&lt;div style=\&quot;text-align:center;heigth=100%\&quot;&gt;&lt;br&gt;&lt;br&gt;正在生成，请稍候...&lt;/div&gt;&quot;);

	//加载数据
	var xml = _sql.query(&quot;MAIN&quot;,&quot;YM1=&quot;+ym1+&quot;&YM2=&quot;+ym2);
	_this.SetMainCellRange(0,3,0,4,3);
	_this.SetXmlDS(0,3,0,4,3,xml);	
	_this.RefreshFormula();
	_this.AutoFixScreenWidth();

	//计算比例
	var sumje = _this.GetCellText(0,_this.GetMainCellRangeRow2(0)+1,1);
	for (var r=_this.GetMainCellRangeRow1(0);r&lt;=_this.GetMainCellRangeRow2(0);r++) {
		var sjje = 1.0 * _this.GetCellText(0,r,1);
		_this.SetCellText(0,r,2,(sjje/sumje*100).toFixed(2));
	}

	setChart(ym1,ym2);

}

//生成分析图
function setChart(ym1,ym2) 
{

	var bldata = getBLData();

