	var gdp = getData(3);

	$(&apos;#chart2&apos;).highcharts({
		        chart: {
		            zoomType: &apos;xy&apos;
		        },
		        title: {
		            text:&apos;税收收入分析（&apos;+ym1.split(&quot;-&quot;)[0]+&apos;年&apos;+ym1.split(&quot;-&quot;)[1]+&apos;月至&apos;+ym2.split(&quot;-&quot;)[0]+&apos;年&apos;+ym2.split(&quot;-&quot;)[1]+&apos;月）&apos;
		        },
		        subtitle: {
		            text: &apos;&apos;
		        },
		        plotOptions: {
		            pie: {
		                allowPointSelect: true,
		                cursor: &apos;pointer&apos;,
		                events: {
		                    click: function(event) {
		                    	//alert(&apos;x=&apos;+event.point.x+&apos; y=&apos;+event.point.y+&apos;data=&apos;+this.data[event.point.x].name);
		                    	var hy = this.data[event.point.x].name;		                    	
