//上月数据,本月数据,上年同期数据
function getData(col)
{
	var data = new Array();
	for(var i = _this.GetMainCellRangeRow1(0);i&lt;=_this.GetMainCellRangeRow2(0);i++) {
		var val = _this.GetCellText(0,i,col);
		if (col != 0) val = 1.0*val;
		if (val == &quot;&quot;) val = 0;
		data.push(val);
	}
	return data;
}

function getBLData()
{
	var data = new Array();
	for(var i = _this.GetMainCellRangeRow1(0);i&lt;=_this.GetMainCellRangeRow2(0);i++) {
		var hy = _this.GetCellText(0,i,0);
		var bl = 1.0*_this.GetCellText(0,i,2);
		var rowdata = new Array();
		rowdata.push(hy);
		rowdata.push(bl);

		data.push(rowdata);
	}
	return data;
}

function filter()
{
	ym1 = G_LOGDAT.split(&quot;-&quot;)[0] + &quot;-01&quot;;
	ym2 =  G_LOGDAT.substring(0,7);
	var ret = window.showModalDialog(&quot;show.sp?grdid=SELECTYM12&quot;,window,&quot;scroll:no;status:no;dialogWidth:300px;dialogHeight:220px&quot;);
	if (ret != &quot;&quot; && ret != undefined) {	
		ym1 = ret.split(&quot;,&quot;)[0];
		ym2 = ret.split(&quot;,&quot;)[1];
		loadData(ym1,ym2);
	}
}


//生成统计数据
function genData()
{

	var ret = window.showModalDialog(&quot;show.sp?grdid=SELECTYM12&quot;,window,&quot;scroll:no;status:no;dialogWidth:300px;dialogHeight:220px&quot;);
	if (ret != &quot;&quot; && ret != undefined) {	
		ym1 = ret.split(&quot;,&quot;)[0];
		ym2 = ret.split(&quot;,&quot;)[1];
		if (!confirm(&quot;生成&quot;+ym1+&quot;至&quot;+ym2+&quot;之间的统计数据，是否继续？&quot;)) return;
		//var ret = _this.invokeOSFunc(&quot;genData&quot;,&quot;ym1=&quot;+ym1+&quot;&ym2=&quot;+ym2);
		var ret = showModalDialog(&quot;show.sp?grdid=Waiting&quot;,window,&quot;scroll:no;status:no;dialogWidth=200px;dialogHeight=80px&quot;);	
		alert(ret);
	}

}

function runWaitingTask()
{
	try {
		var ret = _this.invokeOSFunc(&quot;genData&quot;,&quot;ym1=&quot;+ym1+&quot;&ym2=&quot;+ym2);
		return ret;
	}catch(e) { alert(e.toString()); }
}

