var ZXGFILE0 = &quot;&quot;;
var cur_yyyy = &quot;&quot;;

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.AutoFixScreenWidth();

	ZXGFILE0 = _this.SaveTempZXGFile(0);

	cur_yyyy =  G_LOGDAT.substring(0,4);
	//loadData(cur_yyyy,&quot;&quot;);
	setTimeout(&quot;loadData(&apos;&quot;+cur_yyyy+&quot;&apos;,&apos;&apos;)&quot;,100);

}


//加载电力数据
function loadData(yyyy,hy)
{
	cur_yyyy = yyyy;
	_this.LoadZXGFile(ZXGFILE0,-1,0);
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this.SELECTFLAG_SINGLEROW);
	_this.SetFixedCol(0,0);
	_this.SetFixedRow(0,3);

	_this.SetCellText(0,0,0,_this.GetCellText(0,0,0)+&quot;（&quot;+yyyy+&quot;年）&quot;);
	$(&apos;#container&apos;).html(&quot;&lt;div style=\&quot;text-align:center;heigth=100%\&quot;&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;正在生成，请稍候...&lt;/div&gt;&quot;);

	//电力数据
	var xml = _sql.query(&quot;DLDATA&quot;,&quot;HY=&quot;+hy+&quot;&YYYY=&quot;+yyyy);
	_this.SetXmlDS(0,3,0,14,5,xml);	
	//税收收入数据
	 xml = _sql.query(&quot;TAXDATA&quot;,&quot;HY=&quot;+hy+&quot;&YYYY=&quot;+yyyy);
	_this.SetXmlDS(0,3,6,14,10,xml);

	//设置上月数	
	for (var r=4;r&lt;=14;r++) {
		_this.SetCellText(0,r,2,_this.GetCellText(0,r-1,1));
		_this.SetCellText(0,r,7,_this.GetCellText(0,r-1,6));
	}

	_this.SetCellText(0,3,2,_this.GetCellText(0,14,3)); //12月份的上年同期
	_this.SetCellText(0,3,7,_this.GetCellText(0,14,8)); //12月份的上年同期

	jsbl();

	var hyname = &quot;&quot;;
	setChart(yyyy,hyname);

}


//计算比例
function jsbl()
{
	for(var i = 3;i&lt;=14;i++){
		var by = 1*_this.GetCellText(0,i,1);
		var sy = 1*_this.GetCellText(0,i,2);
		var sntq = 1*_this.GetCellText(0,i,3);
		if (sy != 0 && sy != &quot;&quot;) {
			var bl = convertRound(by/sy,2);
			if (bl &lt; 1) bl = &quot;&quot;+bl;
			_this.SetCellText(0,i,4,bl);
		}
		if (sntq != 0 && sntq != &quot;&quot;) {
			var bl = convertRound(by/sntq,2);
			if (bl &lt; 1) bl = &quot;&quot;+bl;
			_this.SetCellText(0,i,5,bl);
		}

		var by2 = 1*_this.GetCellText(0,i,6);
		var sy2 = 1*_this.GetCellText(0,i,7);
		var sntq2 = 1*_this.GetCellText(0,i,8);
		if (sy2 != 0 && sy2 != &quot;&quot;) {
			var bl = convertRound(by2/sy2,2);
			if (bl &lt; 1) bl = &quot;&quot;+bl;
			_this.SetCellText(0,i,9,bl);
		}
		if (sntq2 != 0 && sntq2 != &quot;&quot;) {
			var bl = convertRound(by2/sntq2,2);
			if (bl &lt; 1) bl = &quot;&quot;+bl;
			_this.SetCellText(0,i,10,bl);
		}
	}

}

function convertRound(ADoubleInput,ADotNumber){
    var tmpValue1=1;
    var tmpValue2=1;
    if (ADotNumber&lt;0) return ADoubleInput;
    if (0==ADoubleInput) return Math.round(ADoubleInput);
    for (i=0;i&lt;ADotNumber;i++){
      tmpValue1=tmpValue1*10;
    }
    tmpValue2=ADoubleInput*tmpValue1;
    return Math.round(tmpValue2)/tmpValue1;
}

function setChart(yyyy,hy) 
