	var sydata2 = getData(7);
	var sntqdata2 = getData(8);
	var hbdata2 = getData(9);
	var tbdata2 = getData(10);

	$(&apos;#container&apos;).highcharts({
		        chart: {
		            zoomType: &apos;xy&apos;
		        },
