		        },
		        xAxis: [{
		            categories: [&apos;一月&apos;,&apos;二月&apos;,&apos;三月&apos;,&apos;四月&apos;,&apos;五月&apos;,&apos;六月&apos;,&apos;七月&apos;,&apos;八月&apos;, &apos;九月&apos;,&apos;十月&apos;,&apos;十一月&apos;,&apos;十二月&apos;],
		            crosshair: true
		        }],
		        yAxis: [{
		            min: 0,		            
		            title: {
		                text: &apos;税收收入（万元）&apos;
		            },
			    tickInterval: 10000
		        },{		            
		            title: {
		                text: &apos;用电量（万度）&apos;
		            },
			    opposite: true,
			    min:0,
			    tickInterval: 100000
		        }],
		        tooltip: {
		            shared: true
