		        }, {
		            name: &apos;收入-本月&apos;,
		            type: &apos;column&apos;,
		            yAxis: 0,
		            data: bydata2
			}, {
		            name: &apos;收入-上年同期&apos;,
		            type: &apos;column&apos;,
		            yAxis: 0,
		            data: sntqdata2
		        }, {
		            name: &apos;税收收入&apos;,
		            type: &apos;line&apos;,
			    yAxis: 0,
		            data: bydata2,
			    dataLabels: {
			       enabled: false,
		               align: &apos;center&apos;
			    }
		        }, {
