}

//上月数据,本月数据,上年同期数据
function getData(col)
{
	var data = new Array();
	for(var i = 3;i&lt;=14;i++) {
		var val = 1*_this.GetCellText(0,i,col);
		if (val != &quot;&quot;) data.push(val);
	}
	return data;
}


function lastYear()
{
	loadData(1*cur_yyyy - 1,&quot;&quot;)
}

function nextYear()
{
	loadData(1*cur_yyyy + 1,&quot;&quot;)
}

