ROW.GRDJSDS_VALUE:
var sheet=0;
var bl1 = 0;
var bl2 = 0;
var bl3 = 0;

//数据加载完毕
function _user_Onpost_loaddata(sheet)
{
        _this.ShowToolBar(1);
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHOW_ZERO,1);
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this.SELECTFLAG_SINGLEROW);
	_this.SetToBoolBox(sheet,5,1);


	var chkrol = invokeOSFunc(&quot;GXHZTAX.DesktopLayout.checkGenTask2&quot;,&quot;usrid=&quot;+G_USRID+&quot;&thisorgid=&quot;+G_ORGID);
	if (chkrol &gt; 0) {

	}else{
		_this.AddToolbarButton(&quot;udf_genTasks&quot;,&quot;生成核实任务&quot;,&quot;&quot;,&quot;&quot;,0,0,0,100);

		_sql.querytods(&quot;FXDJBL&quot;,&quot;&quot;); //在循环外查询出等级比例
		bl1 = _this.XMLDS_GetString(0,&quot;BL&quot;); //高
		bl2 = _this.XMLDS_GetString(1,&quot;BL&quot;); //中
		bl3 = _this.XMLDS_GetString(2,&quot;BL&quot;); //低

	}
	var row = _this.GetRowCount(sheet);
	var cz = 0;

	//预警等级
	for(var row=6;row&lt;=_this.GetMainCellRangeRow2(sheet);row++){
		if (_this.GetCellText(0,row,2) != &quot;&quot;) {
			_this.SetToBoolBox(sheet,row,1);
		}
		var cssomny = _this.GetCellText(0,row,8);
		var sbmny = _this.GetCellText(0,row,9); 
		var cz = (cssomny - sbmny).toFixed(2);
		var bl = (cz / cssomny).toFixed(2);
		if (bl &gt; 0) {
			if (bl &gt;= bl1) { _this.SetCellText(0,row,11,&quot;高&quot;); _this.SetCellColor(0,row,11,255,0,0); }
			else if (bl &gt;= bl2) { _this.SetCellText(0,row,11,&quot;中&quot;); _this.SetCellColor(0,row,11,230,165,25); }
			else if (bl &gt;= bl3) { _this.SetCellText(0,row,11,&quot;低&quot;); _this.SetCellColor(0,row,11,220,220,110); }
		}
		else {
			_this.SetCellText(0,row,11,&quot;&quot;);
		}
	}

	_this.AutoFixScreenWidth();
}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
  if(row==5 && col==1){
  	for(var r=5;r&lt;=_this.GetMainCellRangeRow2(sheet);r++){
  		_this.SetCellText(sheet,r,1,newvalue);
  	}
  }
}

//生成核实任务
function genTasks()
{	
	var param = new Object();	

	var xmlds = getXmlStr(0,6,1,_this.GetMainCellRangeRow2(0),13);
	param.xmlstr = xmlds;	
	param.usrids = G_USRID;
	param.usrnams = G_USRNAM;
	param.accids = G_ACCID;
	param.orgs = G_ORGID;
	param.syts = G_SYTID;
	param.typ = &quot;2&quot;;     //决策二包任务
	param.subtyp = &quot;02&quot;; //电力类 
	param.yymm1 = YYMM1;
	param.yymm2 = YYMM2;
	param.swjg_dm = G_DEPTID;
	param.toswjg = &quot;&quot;;
	param.tousr=&quot;&quot;;

	var retobj = window.showModalDialog(&quot;show.sp?grdid=RWParam&typ=&quot;+param.typ+&quot;&subtyp=&quot;+param.subtyp,&quot;&quot;,&quot;dialogHeight=300px;dialogWidth=420px;toolbar=no;menubar=no;scrollbars=no;resizable=no;location=no;status=no&quot;);
	try {
	//if (retobj != &quot;undefined&quot; && retobj != null) {		
		param.enddat = retobj.enddat;
		param.note = retobj.note;
		param.typ = retobj.typ;
		param.subtyp = retobj.subtyp;
		var msg = invokeOSFuncExt(&quot;TAXFXJK.PUBTRK.createTask&quot;,param);
		alert(msg);
	//}
	} catch (e) { }

}


function getXmlStr(sheet,row1,col1,row2,col2)
{
	var xmlstr = &quot;&lt;?xml version=&apos;1.0&apos;?&gt;&lt;ROWSET&gt;&quot;;
	var i = 0;
	for (var r=row1;r&lt;=row2;r++) {
		if (_this.GetCellText(sheet,r,1) == &quot;1&quot;) {
			xmlstr += &quot;&lt;ROW num=\&quot;&quot;+(++i)+&quot;\&quot;&gt;\n&quot;;
			var nsrsbh = _this.GetCellText(sheet,r,4);
			var nsrmc = _this.GetCellText(sheet,r,5);
			var csxsje = 1.0*_this.GetCellText(sheet,r,8); //测算收入金额
			var sbje = 1.0*_this.GetCellText(sheet,r,9); //申报金额
			var cyje = Math.abs(sbje - csxsje).toFixed(2); //差异金额
			var note = &quot;测算收入金额：&quot;+csxsje+&quot;，申报金额：&quot;+sbje+&quot;，差异金额：&quot;+cyje;
			var fxdj = _this.GetCellText(sheet,r,11);

			xmlstr += &quot;&lt;NSRSBH&gt;&quot;+nsrsbh+&quot;&lt;/NSRSBH&gt;\n&quot;; //纳税人识别号
			xmlstr += &quot;&lt;NSRMC&gt;&quot;+nsrmc+&quot;&lt;/NSRMC&gt;\n&quot;;	   //纳税人名称
			xmlstr += &quot;&lt;NOTE&gt;&quot;+note+&quot;&lt;/NOTE&gt;\n&quot;;       //说明
			xmlstr += &quot;&lt;FXDJ&gt;&quot;+fxdj+&quot;&lt;/FXDJ&gt;\n&quot;;       //风险等级
			xmlstr += &quot;&lt;CYJE&gt;&quot;+cyje+&quot;&lt;/CYJE&gt;\n&quot;;       //差异金额
			xmlstr += &quot;&lt;/ROW&gt;\n&quot;;
		}
	}
	xmlstr += &quot;&lt;/ROWSET&gt;&quot;;
	return xmlstr;
}

