//替换SQL参数
function replaceParam(mwobj,request,sql)
{
	var gds = request.getParameter(&quot;GDS&quot;);
	if (gds != &quot;&quot;) {
		sql = pub.EAFunc.Replace(sql,&quot;[%GDSSTR]&quot;,&quot;and nvl(a.gds,&apos;&apos;) IN (select name from v_tax_gds where ID like &apos;&quot;+gds+&quot;%&apos;)&quot;);
	}
	else {
		sql = pub.EAFunc.Replace(sql,&quot;[%GDSSTR]&quot;,&quot;&quot;);
	}

	var usrinfo = web.EASession.GetLoginInfo(request);
	var usrorg = usrinfo.getusrOrg();
	var usrid = usrinfo.getUsrid();
	var swjg_dm = request.getParameter(&quot;SWJG_DM&quot;);
	if (swjg_dm == &quot;1451100&quot;) {
		sql = pub.EAFunc.Replace(sql,&quot;[%SWJGSTR]&quot;,&quot; &quot;);
	}
	else {
		sql = pub.EAFunc.Replace(sql,&quot;[%SWJGSTR]&quot;,&quot; and b.swjg_dm like &apos;&quot;+swjg_dm+&quot;%&apos;&quot;);
	}


	return sql;
}

ROW.DATDSC:
select &apos;&apos; flag,&apos;2011-01&apos;||&apos;~&apos;||&apos;2011-12&apos; YYMM,a.id,a.name,sum(eleqty) eleqty,envload,sum(ens) ens,
       sum(somny) somny,sum(ens-somny) cz,Ytaxman,taxman 
from (
select a.yymm,a.usrid,a.usrnam,sum(a.eleqty) eleqty,nvl(round(sum(a.eleqty/c.envload*avgsale),2),0) ens,c.envload,
   decode(b.Ytaxman,&apos;1&apos;,&apos;是&apos;,&apos;否&apos;) ytaxman,b.taxman,b.id,b.name
from tax_eledata a,tax_company b,tax_compclass c 
where a.usrid=b.ammno and b.Typclsid=c.id
  and nvl(b.towns,&apos; &apos;) IN (select name from V_TAX_TOWNS WHERE ID like &apos;[%TOWNS]%&apos;)
  and c.id=&apos;[%CORPID]&apos; and b.stat != &apos;注销&apos;
  and a.YYMM between &apos;[%YYMM1]&apos; and &apos;[%YYMM2]&apos;
group by a.yymm,a.usrid,a.usrnam,b.id,b.name,b.Ytaxman,b.taxman,c.envload 
) a,(
select a.yymm,a.id,a.name,sum(a.somny) somny,b.ammno
from tax_taxdata a,v_tax_company b,tax_compclass c where a.id=b.id and b.Typclsid=c.id
  and nvl(b.towns,&apos; &apos;) IN (select name from V_TAX_TOWNS WHERE ID like &apos;[%TOWNS]%&apos;)
  and c.id=&apos;[%CORPID]&apos; and b.stat != &apos;注销&apos;
  and a.YYMM between &apos;[%YYMM1]&apos; and &apos;[%YYMM2]&apos;
group by a.yymm,a.id,a.name,b.ammno
  ) b
where a.yymm=b.yymm(+) and a.usrid=b.ammno(+)  
and nvl(somny,0) &lt; ens
AND nvl(somny,0) between decode(&apos;[%STRSALE]&apos;,&apos;&apos;,0,&apos;[%STRSALE]&apos;) and decode(&apos;[%TOSALE]&apos;,&apos;&apos;,10000000000,&apos;[%TOSALE]&apos;) 
group by a.id,a.name,a.envload,Ytaxman,taxman

select flag,rownum rn,yymm,id,name,eleqty,envload,ens,somny,
	cz,&apos;&apos;fxdj,ytaxman,taxman,swjg
from (
select flag,rownum rn,yymm,
	(select nvl(shxydm,id) from tax_company cp where cp.id=aa.id ) id,
	aa.name,eleqty,envload,ens,somny,
	cz,ytaxman,taxman,bb.name swjg 
from (
select &apos;&apos; flag,&apos;[%YYMM1]&apos;||&apos;~&apos;||&apos;[%YYMM2]&apos; YYMM,
	a.id,a.name,sum(eleqty) eleqty,envload,sum(ens) ens,
       max(somny) somny,sum(ens)-max(somny) cz,Ytaxman,taxman,swjg 
from (
select b.djxh,b.id,sum(a.eleqty) eleqty,
	round((eleqty-eleqty*nvl(c.eleload,0))/c.envload*c.avgsale,2) ens,
	c.envload,decode(b.Ytaxman,&apos;1&apos;,&apos;是&apos;,&apos;否&apos;) ytaxman,e.name taxman,b.name,
	substr(max(b.swjg_dm),0,7) swjg
from tax_eledata a,tax_company b,tax_compclass c,tax_cmpammno d,v_tax_taxman e
where a.usrid=d.ammno and b.Typclsid=c.id and b.id=d.cmid and b.taxman=e.uuid(+)
  [%GDSSTR]
  [%SWJGSTR]
  and c.id=&apos;[%CORPID]&apos;
  and c.year=(SELECT SUBSTR(&apos;[%YYMM1]&apos;,0,4) FROM DUAL) 
  and c.eff=&apos;1&apos;
  and b.hymx_dm like &apos;[%GBHY]%&apos;
  and a.YYMM between &apos;[%YYMM1]&apos; and &apos;[%YYMM2]&apos;
  and (b.id like &apos;%[%CMID]%&apos; or b.name like &apos;%[%CMID]%&apos;)
  and b.taxman like &apos;%[%TAXMAN]%&apos;
group by b.djxh,b.id,b.name,b.Ytaxman,e.name,c.envload,eleqty,c.eleload,c.avgsale
) a,(
select djxh,sum(somny) somny from tax_taxdata 
where YYMM between &apos;[%YYMM1]&apos; and &apos;[%YYMM2]&apos; group by djxh
) d
where a.djxh=d.djxh
group by a.djxh,a.id,a.name,envload,ytaxman,taxman,swjg
) aa,v_swjg bb
where aa.swjg=bb.ID(+)
AND nvl(somny,0) between decode(&apos;[%STRSALE]&apos;,&apos;&apos;,0,&apos;[%STRSALE]&apos;) 
and decode(&apos;[%TOSALE]&apos;,&apos;&apos;,10000000000,&apos;[%TOSALE]&apos;) 
order by cz desc
)
