//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this.SELECTFLAG_SINGLEROW);
	for(var row=5;row&lt;=_this.GetMainCellRangeRow2(sheet);row++)
	{
		_this.SetToBoolBox(sheet,row,1);
	}

	var chkrol = invokeOSFunc(&quot;GXHZTAX.DesktopLayout.checkGenTask2&quot;,&quot;usrid=&quot;+G_USRID+&quot;&thisorgid=&quot;+G_ORGID);
	if (chkrol &gt; 0) {

	}else{
		_this.AddToolbarButton(&quot;udf_genTasks&quot;,&quot;生成核实任务&quot;,&quot;&quot;,&quot;&quot;,0,0,0,100);
		var Addmenu = &quot;&lt;item id=\&quot;isNoGY\&quot; name=\&quot;非工业纳税企业\&quot;/&gt;&lt;item id=\&quot;isGY\&quot; name=\&quot;工业纳税企业\&quot;/&gt;&quot;;
		_this.AddToolbarButton(&quot;&quot;,&quot;标志为&quot;,&quot;&quot;,Addmenu,0,0,0,70);
	}

	_sql.querytods(&quot;FXJB&quot;,&quot;&quot;);
	var fxjb = _this.XMLDS_GetString(0,&quot;FXJB&quot;);
	for(var r=6;r&lt;=_this.GetMainCellRangeRow2(sheet);r++){
		//_this.SetCellBkColor(sheet,i,9,255,0,0);
		//_this.SetCellText(sheet,i,9,&quot;&quot;);
		if (fxjb == &quot;高&quot;) { _this.SetCellText(0,r,9,&quot;高&quot;); _this.SetCellColor(0,r,9,255,0,0); }
		else if (fxjb == &quot;中&quot;) { _this.SetCellText(0,r,9,&quot;中&quot;); _this.SetCellColor(0,r,9,230,165,25); }
		else if (fxjb == &quot;低&quot;) { _this.SetCellText(0,r,9,&quot;低&quot;); _this.SetCellColor(0,r,9,220,220,110); }

	}

	_this.AutoFixScreenWidth();
}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
  if(row==5 && col==1)
  {
  	for(var r=5 ;r&lt;=_this.GetMainCellRangeRow2(sheet);r++){
  		_this.SetCellText(sheet,r,1,newvalue);		
    	}
   }
}

function getSelectRow()
{
	var comps = &quot;&quot;;
	for (var r=_this.GetMainCellRangeRow1(0);r&lt;=_this.GetMainCellRangeRow2(0);r++) {
		if (_this.GetCellText(0,r,1) == &quot;1&quot;) {
			var id = _this.GetCellText(0,r,3);
			if (comps == &quot;&quot;) comps += id;
			else comps += &quot;,&quot; + id;
		}
	}
	return comps;
}
//非国税管理企业
function isnoGYCompany()
{
	if (!confirm(&quot;是否标志为非工业纳税企业？&quot;)) return;
	var comps = getSelectRow();

	if (comps != &quot;&quot;) {
		var param = new Object();
		param.cms = comps;
		param.thisusrid = G_USRID;
		var ret = invokeOSFuncExt(&quot;updateFlag&quot;,param);
		alert(ret);
	}
}

//国税管理企业
function isGYCompany()
{
	if (!confirm(&quot;是否标志工业纳税企业？&quot;)) return;
	var comps = getSelectRow();

	if (comps != &quot;&quot;) {
		var param = new Object();
		param.cms = comps;
		param.thisusrid = G_USRID;
		var ret = invokeOSFuncExt(&quot;updateFlag2&quot;,param);
		alert(ret);
	}
}

//点击菜单项
function _thisOnMenuItemSelect(sheet,row,col,menuitemid)
{
	if (menuitemid == &quot;isNoGY&quot;) {
		isnoGYCompany();
	}
	else if (menuitemid == &quot;isGY&quot;) {
		isGYCompany();
	}
}


//生成核实任务
function genTasks()
{	
	var param = new Object();	

	var xmlds = getXmlStr(0,6,1,_this.GetMainCellRangeRow2(0),10);
	param.xmlstr = xmlds;	
	param.usrids = G_USRID;
	param.usrnams = G_USRNAM;
	param.accids = G_ACCID;
	param.orgs = G_ORGID;
	param.syts = G_SYTID;
	param.typ = &quot;1&quot;;     //平台内部任务
	param.subtyp = &quot;02&quot;; //电力类 
	param.yymm1 = G_LOGDAT;
	param.yymm2 = G_LOGDAT;
	param.swjg_dm = G_DEPTID;
	param.toswjg = &quot;&quot;;
	param.tousr=&quot;&quot;;

	var retobj = window.showModalDialog(&quot;show.sp?grdid=RWParam&typ=&quot;+param.typ+&quot;&subtyp=&quot;+param.subtyp,&quot;&quot;,&quot;dialogHeight=300px;dialogWidth=420px;toolbar=no;menubar=no;scrollbars=no;resizable=no;location=no;status=no&quot;);
	try {
	//if (retobj != &quot;undefined&quot; && retobj != null) {		
		param.enddat = retobj.enddat;
		param.note = retobj.note;
		param.typ = retobj.typ;
		param.subtyp = retobj.subtyp;
		var msg = invokeOSFuncExt(&quot;TAXFXJK.PUBTRK.createTask&quot;,param);
		alert(msg);
	//}
	} catch (e) { }

}


function getXmlStr(sheet,row1,col1,row2,col2)
{
	var xmlstr = &quot;&lt;?xml version=&apos;1.0&apos;?&gt;&lt;ROWSET&gt;&quot;;
	var i = 0;
	for (var r=row1;r&lt;=row2;r++) {
		if (_this.GetCellText(sheet,r,1) == &quot;1&quot;) {
			xmlstr += &quot;&lt;ROW num=\&quot;&quot;+(++i)+&quot;\&quot;&gt;\n&quot;;
			var nsrsbh = _this.GetCellText(sheet,r,4);
			var nsrmc = _this.GetCellText(sheet,r,5);
			var csxsje = &quot;&quot;;// _this.GetCellText(sheet,r,6); //测算收入金额
			var sbje = &quot;&quot;;//_this.GetCellText(sheet,r,7); //申报金额
			var cyje = &quot;&quot;; //差异金额
			var note = &quot;&quot;;
			var fxdj = _this.GetCellText(sheet,r,9);

			xmlstr += &quot;&lt;NSRSBH&gt;&quot;+nsrsbh+&quot;&lt;/NSRSBH&gt;\n&quot;; //纳税人识别号
			xmlstr += &quot;&lt;NSRMC&gt;&quot;+nsrmc+&quot;&lt;/NSRMC&gt;\n&quot;;	   //纳税人名称
			xmlstr += &quot;&lt;NOTE&gt;&quot;+note+&quot;&lt;/NOTE&gt;\n&quot;;       //说明
			xmlstr += &quot;&lt;FXDJ&gt;&quot;+fxdj+&quot;&lt;/FXDJ&gt;\n&quot;;       //风险等级
			xmlstr += &quot;&lt;CYJE&gt;&quot;+cyje+&quot;&lt;/CYJE&gt;\n&quot;;       //差异金额
			xmlstr += &quot;&lt;/ROW&gt;\n&quot;;
		}
	}
	xmlstr += &quot;&lt;/ROWSET&gt;&quot;;
	return xmlstr;
}

