//替换SQL参数
function replaceParam(mwobj,request,sql)
{
var usrinfo = web.EASession.GetLoginInfo(request);
	var usrorg = usrinfo.getusrOrg();
	var usrid = usrinfo.getUsrid();
	var swjg_dm = request.getParameter(&quot;SWJG_DM&quot;);
	if (swjg_dm == &quot;1451100&quot;) {
		sql = pub.EAFunc.Replace(sql,&quot;[%SWJGSTR]&quot;,&quot; &quot;);
	}
	else {
		sql = pub.EAFunc.Replace(sql,&quot;[%SWJGSTR]&quot;,&quot; and d.swjg_dm like &apos;&quot;+swjg_dm+&quot;%&apos;&quot;);
	}

	return sql;
}

ROW.DATDSC:
select flag,rownum rn,id,name,eleqty,elemny,somny,cz,&apos;&apos; fxdj,taxman,swjg 
from (
    select flag,rownum rn,id,name,eleqty,elemny,somny,elemny-somny cz,taxman,
      (select name from v_swjg sw where sw.id=substr(swjg_dm,0,7)) swjg 
    from (
	select &apos;&apos; flag,d.swjg_dm,nvl(d.shxydm,aa.cmid) id,max(aa.cmnam) name,sum(aa.eleqty) eleqty,
		sum(aa.elemny) elemny,nvl(sum(c.somny),1) somny,max(e.name) taxman 
	from (
		select b.cmid,sum(a.eleqty) eleqty,sum(a.elemny) elemny,max(b.cmnam) cmnam
		from tax_eledata a,tax_cmpammno b
		where yymm &gt;= &apos;[%YYMM1]&apos; and yymm &lt;= &apos;[%YYMM2]&apos; 
		and a.usrid=b.ammno(+)
		group by b.cmid 
	    ) aa,(
		select id,sum(somny) somny from tax_taxdata 
		where yymm &gt;= &apos;[%YYMM1]&apos; and yymm &lt;= &apos;[%YYMM2]&apos; 
		group by id
	    ) c,tax_company d,v_tax_taxman e
	where aa.cmid like &apos;[%CMID]%&apos; 
	  and aa.eleqty between decode(&apos;[%ETY1]&apos;,&apos;&apos;,0,&apos;[%ETY1]&apos;) 
	  and decode(&apos;[%ETY2]&apos;,&apos;&apos;,9999999999,&apos;[%ETY2]&apos;) 
	  and aa.cmid=c.id(+) and c.id=d.id(+) and d.taxman=e.uuid(+)
	  and d.typclsid like &apos;[%CORPID]%&apos;
	  and d.taxman like &apos;%[%TAXMAN]%&apos;
	  [%SWJGSTR]
	group by d.swjg_dm,nvl(d.shxydm,aa.cmid)
    ) order by swjg_dm,cz desc
)
