ROW.GRDJSDS_VALUE:
var ZXGFILE0 = &quot;&quot;;
var ZXGFILE1 = &quot;&quot;;
var hyList;

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SplitSheet(2,&quot;V&quot;,&quot;10%&quot;);
	_this.SplitSheet(0,&quot;V&quot;,&quot;30%&quot;);
	ZXGFILE0 = _this.SaveTempZXGFile(0);
	ZXGFILE1 = _this.SaveTempZXGFile(1);

	hyList = _this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_TAX_STONE_HY&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	_this.SetToComboBox(&quot;&quot;,2,1,2,hyList);
	_this.SetCellText(2,1,2,&quot;1&quot;); //默认粉体行业

	//加载行业数据
	loadHYCS();
	loadHY();

	_this.AutoFixScreenWidth();

}

//加载行业参数
function loadHYCS()
{
	_this.LoadZXGFile(ZXGFILE0 ,-1,0);
	_this.SetFixedRow(0,1);
	_this.SetMainCellRange(0,1,2,3,13);
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this.SELECTFLAG_SINGLEROW);

	var hydm = _this.GetCellText(2,1,2);
	var xml = _sql.query(&quot;HYCS&quot;,&quot;HYDM=&quot;+hydm);
	_this.SetXmlDS(0,1,2,3,13,xml);

	//hydm,csid,csmc,dj,whbl,LFT,LFM,DHY,DWYH,hlsj,bcsj,sssj
	_this.SetColName(0,2,&quot;HYDM&quot;);
	_this.SetColName(0,3,&quot;CSID&quot;);
	_this.SetColName(0,4,&quot;CSMC&quot;);
	_this.SetColName(0,5,&quot;DJ&quot;);
	_this.SetColName(0,6,&quot;WHBL&quot;);
	_this.SetColName(0,7,&quot;LFT&quot;);
	_this.SetColName(0,8,&quot;LFM&quot;);
	_this.SetColName(0,9,&quot;DHY&quot;);
	_this.SetColName(0,10,&quot;DWYH&quot;);
	_this.SetColName(0,11,&quot;HLSJ&quot;);
	_this.SetColName(0,12,&quot;BCSJ&quot;);
	_this.SetColName(0,13,&quot;SSSJ&quot;);

	for (var r=1;r&lt;_this.GetRowCount(0);r++) {
		_this.SetToBoolBox(0,r,1);
		_this.SetToComboBox(&quot;&quot;,0,r,2,hyList);
		if (_this.GetCellText(0,r,2) == &quot;&quot;) {
			_this.SetRowVisible(0,r,-1);
		}
	}

	//隐藏不同行业需要设置的参数列
	if (hydm == 1) { 
		_this.SetColVisible(0,7,-1);
		_this.SetColVisible(0,8,-1);
		_this.SetColVisible(0,9,-1);
		_this.SetColVisible(0,10,-1);
		_this.SetColVisible(0,11,-1);
		_this.SetColVisible(0,12,-1);
		_this.SetColVisible(0,13,-1);
	}
	else if (hydm == 2) {
		//_this.SetColVisible(0,9,-1);
		_this.SetCellText(0,0,9,&quot;1度电产出税款（元）&quot;);
		_this.SetColVisible(0,10,-1);
		_this.SetColVisible(0,11,-1);
		_this.SetColVisible(0,12,-1);
		_this.SetColVisible(0,13,-1);
	}
	else if (hydm == 3) {
		_this.SetColVisible(0,5,-1);
		_this.SetColVisible(0,6,-1);
		_this.SetColVisible(0,7,-1);
		_this.SetColVisible(0,8,-1);
		_this.SetColVisible(0,10,-1);
		_this.SetColVisible(0,11,-1);
		_this.SetColVisible(0,12,-1);
		_this.SetColVisible(0,13,-1);
	}
	else if (hydm == 4) {
		_this.SetColVisible(0,5,-1);
		_this.SetColVisible(0,7,-1);
		_this.SetColVisible(0,8,-1);
		_this.SetColVisible(0,9,-1);
		_this.SetColVisible(0,10,-1);

	}

}



//加载石材行业数据
function loadHY()
{
	_this.LoadZXGFile(ZXGFILE1 ,-1,1);
	_this.SetFixedRow(1,1);
	var hydm = _this.GetCellText(2,1,2);
	var xml = _sql.query(&quot;GXHHY&quot;,&quot;HYDM=&quot;+hydm);
	_this.SetXmlDS(1,1,1,3,3,xml);

	//flag,id,name
	_this.SetColName(1,1,&quot;FLAG&quot;);
	_this.SetColName(1,2,&quot;ID&quot;);
	_this.SetColName(1,3,&quot;NAME&quot;);

	for (var r=1;r&lt;_this.GetRowCount(1);r++) {
		_this.SetToBoolBox(1,r,1);		
	}

}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if (sheet == 2) {
		loadHYCS();
		loadHY();
		_this.AutoFixScreenWidth();
	}
	else if (sheet == 0 && col ==1) {
		for (var r=1;r&lt;_this.GetRowCount(0);r++) {
			_this.SetCellText(0,r,1,&quot;0&quot;);
		}
		_this.SetCellText(0,row,1,&quot;1&quot;);
	}
	else if (sheet == 1) {

	}
}

//确认
function commit()
{

}

//新增参数值
function addParam()
{
	_this.AppendRow(0,_this.GetRowCount(0)-1);
	var hydm = _this.GetCellText(2,1,2);
	_this.SetCellText(0,_this.GetRowCount(0)-1,2,hydm);
}



//保存
function Save()
{
	if (!confirm(&quot;保存当前修改的数据，是否继续？&quot;)) return;

	try {
		var param = new Object();
		var hydm = _this.GetCellText(2,1,2);
		param.selhydm = hydm;
		param.csxml = _this.GetXml(0,1,2,_this.GetRowCount(0)-1,_this.GetColCount(0)-1);
		param.hyxml = _this.GetXml(1,1,1,_this.GetRowCount(1)-1,_this.GetColCount(1)-1);

		var ret = _this.invokeOSFuncExt(&quot;Save&quot;,param);
		alert(ret);
	}
	catch(e) {
		alert(&quot;保存失败:&quot;+e.toString());
	}

}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

function Save()
{
	var db = null;

	try {
		db = new pub.EADatabase();

		var csds = new pub.EAXmlDS(csxml);
		var sql = &quot;delete from tax_stone_param where hydm=&apos;&quot;+selhydm+&quot;&apos;&quot;;
		db.ExcecutSQL(sql);
		for (var i=0;i&lt;csds.getRowCount();i++) {
			var hydm = csds.getStringAt(i,&quot;HYDM&quot;);
			var csid = csds.getStringAt(i,&quot;CSID&quot;);
			var csmc = csds.getStringAt(i,&quot;CSMC&quot;);
			var dj = csds.getStringAt(i,&quot;DJ&quot;);
			var whbl = csds.getStringAt(i,&quot;WHBL&quot;);
			var lft = csds.getStringAt(i,&quot;LFT&quot;);
			var lfm = csds.getStringAt(i,&quot;LFM&quot;);
			var dhy = csds.getStringAt(i,&quot;DHY&quot;);
			var dwyh = csds.getStringAt(i,&quot;DWYH&quot;);
			var hlsj = csds.getStringAt(i,&quot;HLSJ&quot;);
			var bcsj = csds.getStringAt(i,&quot;BCSJ&quot;);
			var sssj = csds.getStringAt(i,&quot;SSSJ&quot;);

			if (hydm != &quot;&quot; && csid != &quot;&quot;) {
				sql = &quot;insert into tax_stone_param(hydm,csid,csmc,dj,whbl,LFT,LFM,DHY,DWYH,hlsj,bcsj,sssj)
					values(&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;)&quot;
					.format([hydm,csid,csmc,dj,whbl,lft,lfm,dhy,dwyh,hlsj,bcsj,sssj]);
				db.ExcecutSQL(sql);  
			}


		}

		var hyds = new pub.EAXmlDS(hyxml);
		sql = &quot;delete from tax_stone_hy where hydm=&apos;&quot;+selhydm+&quot;&apos;&quot;;
		for (var i=0;i&lt;hyds.getRowCount();i++) {
			var flag = hyds.getStringAt(i,&quot;FLAG&quot;);
			var id = hyds.getStringAt(i,&quot;ID&quot;);
			if (flag == &quot;1&quot;) {
				sql = &quot;insert into tax_stone_hy(hydm,gxhhydm)values(&apos;%s&apos;,&apos;%s&apos;)&quot;.format([selhydm,id]);
				db.ExcecutSQL(sql);
			}
		}

		db.Commit();

		return &quot;保存成功!&quot;;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}

ROW.DATDSC:
select flag,id,name from (
select distinct decode(b.hydm,null,&apos;0&apos;,&apos;1&apos;) flag,a.id,a.name 
from v_tax_scgxhhy a,tax_stone_hy b
where a.id=b.gxhhydm(+) and b.hydm(+)=&apos;[%HYDM]&apos;
--order by b.gxhhydm,a.id
) order by flag desc,id
