ROW.GRDJSDS_VALUE:
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{   
    var yyyy = G_LOGDAT.split(&quot;-&quot;)[0];
    try {
	    if (DAT1 != &quot;&quot;) {
	    	yyyy = DAT1;
	    }
    } catch (e) {

    }

    _this.SetCellText(sheet,0,0,yyyy+&quot;年驾校数据导入检查&quot;);
    for(var row=_this.GetMainCellRangeRow1(sheet);row&lt;=_this.GetMainCellRangeRow2(sheet);row++) {
    	if (_this.GetCellText(0,row,0) != &quot;&quot;) {
	        for(var col=_this.GetMainCellRangeCol1(sheet);col&lt;=_this.GetMainCellRangeCol2(sheet);col++) {
	            var cellval = _this.GetCellText(sheet,row,col);
	            if(cellval &gt; 0) {
	               _this.SetCellText(sheet,row,col,&quot;√&quot;);
	               _this.SetCellColor(sheet,row,col,0,72,255);

	            }
	            else if (cellval == 0) {
	               _this.SetCellText(sheet,row,col,&quot;w&quot;); 
	               _this.SetCellColor(sheet,row,col,255,0,0);
	            }
	        }
	 }
	 else {
	 	_this.SetRowVisible(0,row,-1);
	 }
    }

}
