ROW.GRDJSDS_VALUE:
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.AutoFixScreenWidth();
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this.SELECTFLAG_SINGLEROW);
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHOW_ZERO,1);

	_this.SetCellText(0,2,3,DAT1+&quot; 至 &quot;+DAT2);
	_this.SetToBoolBox(0,3,0);
	for (var r=_this.GetMainCellRangeRow1(0);r&lt;=_this.GetMainCellRangeRow2(0);r++) {
		if (_this.GetCellText(0,r,1) != &quot;&quot;) {
			_this.SetToBoolBox(0,r,0);
		}
	}

	var chkrol = invokeOSFunc(&quot;GXHZTAX.DesktopLayout.checkGenTask2&quot;,&quot;usrid=&quot;+G_USRID+&quot;&thisorgid=&quot;+G_ORGID);
	if (chkrol &gt; 0) {

	}else{
		_this.AddToolbarButton(&quot;udf_genTasks&quot;,&quot;生成核实任务&quot;,&quot;&quot;,&quot;&quot;,0,0,0,100);
	}

}



//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if (row == 3 && col == 0) {
		for(var r=4;r&lt;=_this.GetMainCellRangeRow2(sheet);r++) {
			if (_this.GetCellText(0,r,1) != &quot;&quot;) {
				_this.SetCellText(sheet,r,0,newvalue);
			}
		}

	}  
}


//生成核实任务
function genTasks()
{	
	var param = new Object();	

	var xmlds = getXmlStr(0,4,0,_this.GetMainCellRangeRow2(0),14);
	param.xmlstr = xmlds;	
	param.usrids = G_USRID;
	param.usrnams = G_USRNAM;
	param.accids = G_ACCID;
	param.orgs = G_ORGID;
	param.syts = G_SYTID;
	param.typ = &quot;2&quot;;     //决策二包任务
	param.subtyp = &quot;01&quot;; //发票类 
	param.yymm1 = DAT1;
	param.yymm2 = DAT2;
	param.swjg_dm = G_DEPTID;
	param.toswjg = &quot;&quot;;
	param.tousr=&quot;&quot;;

	var retobj = window.showModalDialog(&quot;show.sp?grdid=RWParam&typ=&quot;+param.typ+&quot;&subtyp=&quot;+param.subtyp,&quot;&quot;,&quot;dialogHeight=300px;dialogWidth=420px;toolbar=no;menubar=no;scrollbars=no;resizable=no;location=no;status=no&quot;);
	try {
	//if (retobj != &quot;undefined&quot; && retobj != null) {		
		param.enddat = retobj.enddat;
		param.note = retobj.note;
		param.typ = retobj.typ;
		param.subtyp = retobj.subtyp;
		var msg = invokeOSFuncExt(&quot;TAXFXJK.PUBTRK.createTask&quot;,param);
		alert(msg);
	//}
	} catch (e) { }

}


function getXmlStr(sheet,row1,col1,row2,col2)
{
	var xmlstr = &quot;&lt;?xml version=&apos;1.0&apos;?&gt;&lt;ROWSET&gt;&quot;;
	var i = 0;
	for (var r=row1;r&lt;=row2;r++) {
		if (_this.GetCellText(sheet,r,0) == &quot;1&quot;) {
			xmlstr += &quot;&lt;ROW num=\&quot;&quot;+(++i)+&quot;\&quot;&gt;\n&quot;;
			var nsrsbh = _this.GetCellText(sheet,r,2);
			var nsrmc = _this.GetCellText(sheet,r,3);
			var kpje = _this.GetCellText(sheet,r,4);
			var sbje = _this.GetCellText(sheet,r,7);
			var fxdj = &quot;高&quot;;//_this.GetCellText(sheet,r,5);
			var cyje = _this.GetCellText(sheet,r,8);
			var note = &quot;开票与申报比对不符（开票金额：&quot;+kpje+&quot;，申报金额：&quot;+sbje+&quot;，差异：&quot;+cyje+&quot;）&quot;;

			xmlstr += &quot;&lt;NSRSBH&gt;&quot;+nsrsbh+&quot;&lt;/NSRSBH&gt;\n&quot;; //纳税人识别号
			xmlstr += &quot;&lt;NSRMC&gt;&quot;+nsrmc+&quot;&lt;/NSRMC&gt;\n&quot;;	   //纳税人名称
			xmlstr += &quot;&lt;NOTE&gt;&quot;+note+&quot;&lt;/NOTE&gt;\n&quot;;       //说明
			xmlstr += &quot;&lt;FXDJ&gt;&quot;+fxdj+&quot;&lt;/FXDJ&gt;\n&quot;;       //风险等级
			xmlstr += &quot;&lt;CYJE&gt;&quot;+cyje+&quot;&lt;/CYJE&gt;\n&quot;;       //差异金额
			xmlstr += &quot;&lt;/ROW&gt;\n&quot;;
		}
	}
	xmlstr += &quot;&lt;/ROWSET&gt;&quot;;
	return xmlstr;
}
