//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.AutoFixScreenWidth();
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this.SELECTFLAG_SINGLEROW);
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHOW_ZERO,1);

	_this.SetCellText(0,2,3,DAT1+&quot; 至 &quot;+DAT2);
	_this.SetToBoolBox(0,3,0);
	for (var r=_this.GetMainCellRangeRow1(0);r&lt;=_this.GetMainCellRangeRow2(0);r++) {
		_this.SetToBoolBox(0,r,0);
	}

	var cur_year = 1 * G_LOGDAT.split(&quot;-&quot;)[0];
	_this.SetCellText(0,4,4,(cur_year )+&quot;年&quot;);
	_this.SetCellText(0,4,5,(cur_year - 1)+&quot;年&quot;);
	_this.SetCellText(0,4,6,(cur_year - 2)+&quot;年&quot;);
	_this.SetCellText(0,4,7,(cur_year - 3)+&quot;年&quot;);

	_this.SetCellText(0,4,8,(cur_year )+&quot;年&quot;);
	_this.SetCellText(0,4,9,(cur_year - 1)+&quot;年&quot;);
	_this.SetCellText(0,4,10,(cur_year - 2)+&quot;年&quot;);
	_this.SetCellText(0,4,11,(cur_year - 3)+&quot;年&quot;);

	_this.SetCellText(0,4,12,(cur_year )+&quot;年&quot;);
	_this.SetCellText(0,4,13,(cur_year - 1)+&quot;年&quot;);
	_this.SetCellText(0,4,14,(cur_year - 2)+&quot;年&quot;);
	_this.SetCellText(0,4,15,(cur_year - 3)+&quot;年&quot;);

	_this.SetCellText(0,4,16,(cur_year )+&quot;年&quot;);
	_this.SetCellText(0,4,17,(cur_year - 1)+&quot;年&quot;);
	_this.SetCellText(0,4,18,(cur_year - 2)+&quot;年&quot;);
	_this.SetCellText(0,4,19,(cur_year - 3)+&quot;年&quot;);


