//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.AutoFixScreenWidth();

	//加载决策二包的导入信息
	var typ = _this.GetCellText(0,5,6); 
	if (typ == 2) {
		_this.ShowTabBar(1);
		loadJC2BINFO(guid);
	}

	var stat = _this.GetCellText(0,6,6);
	if (stat == 5) { //决策二包结果导入后，未自动结束的任务
		_this.AddToolbarButton(&quot;udf_genTrk2&quot;,&quot;生成二次核实任务&quot;,&quot;生成二次核实任务&quot;,&quot;&quot;,0,1,1,80);
	}
}

//保存单据数据后
function _thisOnaftersave(success)
{
	_this.XMLDS_Parse(success);
	var topic = _this.XMLDS_GetString(0,&quot;TOPIC&quot;);
	var retval = _this.XMLDS_GetString(0,&quot;RETVAL&quot;);
	_this.SetCellText(0,6,6,3); //状态改为已处理
	alert(topic);
}

