ROW.GRDJSDS_VALUE:
var ZXGFILE0 = &quot;&quot;;
var ZXGFILE1 = &quot;&quot;;
var yesnoList;

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SplitSheet(0,&quot;H&quot;,&quot;50%&quot;);
	_this.SplitSheet(2,&quot;V&quot;,&quot;8%&quot;);
	_this.SplitSheet(1,&quot;V&quot;,&quot;95%&quot;)
	ZXGFILE0 = _this.SaveTempZXGFile(0);
	ZXGFILE1 = _this.SaveTempZXGFile(1);

	yesnoList = _this.CreateListValue();
	_this.SetListValue(yesnoList,&quot;1&quot;,&quot;是&quot;);
	_this.SetListValue(yesnoList,&quot;0&quot;,&quot;否&quot;);
	_this.SetToRadioBox(&quot;&quot;,0,2,4,yesnoList);
	_this.SetCellText(0,2,4,&quot;0&quot;);

	var key = _this.GetCellText(0,2,2);
	var ppflag = _this.GetCellText(0,2,4);
	loadTreeData(key,ppflag);
	searNSRDJXX(N_page(0));
}

function loadTreeData(key,ppflag)
{
	_this.LoadZXGFile(ZXGFILE0,-1,0);
	_this.SetAttribe(&quot;SHEET_1&quot;,_this.ATTR_SHEET_SELECTFLAG,_this.SELECTFLAG_SINGLEROW);
	_this.SetFixedRow(0,5);//设置固定行不可滚动
	_this.SetFixedCol(0,0);
	_this.SetToRadioBox(&quot;&quot;,0,2,4,yesnoList);
	_this.SetCellText(0,2,2,key);
	_this.SetcellText(0,2,4,ppflag);
	var wherestr = &quot; and A.JIAXMC like &apos;&quot;+escape(&quot;%&quot;)+key+escape(&quot;%&quot;)+&quot;&apos;&quot;;
	if (ppflag == 1) wherestr += &quot; and  A.NSRSBH is not null&quot;;
	else wherestr += &quot; and  A.NSRSBH is null&quot;;

	var xml=_sql.query(&quot;MAIN&quot;,&quot;wherestr=&quot;+wherestr);
	_this.SetMainCellRange(0,5,1,7,4);
	_this.SetXmlDS(0,5,1,7,4,xml);
	for (var r=_this.GetMainCellRangeRow1(0);r&lt;=_this.GetMainCellRangeRow2(0);r++){
		if(_this.GetCellText(0,r,1) !=&quot;&quot;){
			_this.setToboolBox(0,r,0);
		}	
	}
	_this.AutoFixScreenWidth();//自动适应屏幕		
}
function searNSRDJXX(page)
{
	_this.LoadZXGFile(ZXGFILE1,-1,1);
	_this.SetAttribe(&quot;SHEET_1&quot;,_this.ATTR_SHEET_SELECTFLAG,_this.SELECTFLAG_SINGLEROW);
	_this.SetFixedRow(1,2);
	_this.SetFixedCol(1,1);		
	var key=_this.GetCellText(2,1,2);
	var xml =_sql.query(&quot;MCQY&quot;,&quot;key=&quot;+key+&quot;&page=&quot;+page);
	_this.SetMainCellRange(1,2,0,4,3);
	_this.SetXmlDS(1,2,0,4,3,xml);
	_this.AutoFixScreenWidth();//自动适应屏幕

}
function N_page(Xnum){//翻页功能
	var page=&quot;&quot;;
	if(Xnum==0){//首页
		 page=1;
		 _this.SetCellText(3,1,5,page);
	}else if(Xnum==1){//上一页
		page=_this.GetCellText(3,1,5)-1;
		if(page==0) page=1;
		_this.SetCellText(3,1,5,page);
	}else if(Xnum==2){//下一页
		page=(_this.GetCellText(3,1,5)-1)+2;
		_this.SetCellText(3,1,5,page);
	}else{
		page=1;
		_this.SetCellText(3,1,5,page);
	}
	return page;
	}
//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if(sheet==0&&row==2){
		var key = _this.GetCellText(0,2,2);
		var ppflag = _this.GetCellText(0,2,4);
		loadTreeData(key,ppflag);
	}
}
//鼠标左键点击
function _thisOnMouseLClick(sheet,row,col)
{
	if(sheet==2&&row==1&&col==4){
		searNSRDJXX(N_page(0));	
			}
	if (sheet == 0 && row &gt;= 5) {
		for (var r=_this.GetMainCellRangeRow1(0);r&lt;=_this.GetMainCellRangeRow2(0);r++) {
			if (_this.GetCellText(0,r,1) != &quot;&quot;) {
				_this.SetCellText(0,r,0,0);
			}
		}
		_this.SetCellText(0,row,0,1);
	}

	if(sheet==3&&row==1){
		if(col==1) searNSRDJXX(N_page(1));
		if(col==2) searNSRDJXX(N_page(2));

	}

}

//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{//比配
	if(sheet==1&&row&gt;1){
		for(var r=_this.GetMainCellRangeRow1(0);r&lt;=_this.GetMainCellRangeRow2(0);r++){
			if(_this.GetCellText(0,r,0)==&quot;1&quot;){
				_this.SetCellText(0,r,3,_this.GetCellText(1,row,2));
				_this.SetCellText(0,r,4,_this.GetCellText(1,row,1));
			}
		}
	}
}

//保存
function Save()
{
	var param = new Object();
	try {
		param.xmlstr = _this.GetXml(0,_this.GetMainCellRangeRow1(0),_this.GetMainCellRangeCol1(0),
			_this.GetMainCellRangeRow2(0),_this.GetMainCellRangeCol2(0));
		var ret = _this.invokeOSFuncExt(&quot;Save&quot;,param);
		alert(ret);		
	}
	catch (e) {
		alert(&quot;保存失败：&quot;+e.toString());
	}

}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

function Save()
{
	var db=null;
	var cnt=&quot;&quot;;
	var sql=&quot;&quot;;
	try{
		db = new pub.EADatabase();
		var ds = new pub.EAXmlDS(xmlstr);
		var cnt = 0;
		//return xmlstr;
		for (var i=0;i&lt;ds.getRowCount();i++) {
			var jiaxmc=ds.getStringAt(i,&quot;JIAXMC&quot;);
			var nsrsbh=ds.getStringAt(i,&quot;NSRSBH&quot;);
			var nsrmc = ds.getStringAt(i,&quot;NSRMC&quot;);
			if(jiaxmc !=&quot;&quot;&&nsrsbh !=&quot;&quot;&&nsrmc !=&quot;&quot;){
				sql=&quot;update TAX_JXMC_NSRSBH_DZB a set nsrsbh=&apos;&quot;+nsrsbh+&quot;&apos;,nsrmc=&apos;&quot;+nsrmc+&quot;&apos; where JIAXMC=&apos;&quot;+jiaxmc+&quot;&apos;&quot;;
				cnt+=db.ExcecutSQL(sql);
			}
		}
		db.Commit();
		return &quot;更新驾校数据成功！更新记录数&quot;+cnt;	
	}
	catch(e){
		if(db !=null)db.Rollback();
		return e.toString();	
	}
	finally{
		if(db !=null)db.Close();
	}
}
