//等待时执行的任务
function runWaitingTask()
{
	var xml =  _sql.query(&quot;MAIN&quot;,&quot;DAT1=&quot;+DAT1+&quot;&KPZJE=&quot;+KPZJE+&quot;&ZGSWJG=&quot;+ZGSWJG);
	return xml;
}

function loadData()
{
	//_this.LoadZXGFile(ZXGFILE0,-1,0);
	_this.SetFixedRow(0,1);
	_this.SetFixedCol(0,2);
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this.SELECTFLAG_SINGLEROW);

	var xml = showModalDialog(&quot;show.sp?grdid=Waiting&quot;,window,&quot;scroll:no;status:no;dialogWidth=200px;dialogHeight=80px&quot;);
	_this.SetMainCellRange(0,1,1,_this.GetRowCount(0)-1,_this.GetColCount(0)-1);
	_this.SetXmlDS(0,1,1,_this.GetRowCount(0)-1,_this.GetColCount(0)-1,xml);

	//设置勾选列
	_this.SetToBoolBox(0,0,0);
	for (var r=_this.GetMainCellRangeRow1(0);r&lt;=_this.GetMainCellRangeRow2(0);r++) {
		if (_this.GetCellText(0,r,1) != &quot;&quot;) {
			_this.SetToBoolBox(0,r,0);
		}
	}
	//_this.AutoFixColWidth(0,600);
	_this.AutoFixScreenWidth();

}


//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	if (sheet == 0 && row &gt;= _this.GetMainCellRangeRow1(0) && row &lt;= _this.GetMainCellRangeRow2(0)) {
		var nsrdjxh=_this.GetCellText(sheet,row,2);
		//window.open(&quot;show.sp?grdid=ZDSYLIST_1&nsrdjxh=&quot;+nsrdjxh);
		var ym1 = G_LOGDAT.substring(0,7);
		var ym2 = G_LOGDAT.substring(0,7);
		window.open(&quot;Layout.sp?id=zdsyyhs&DJXH=&quot;+nsrdjxh+&quot;&YM1=&quot;+ym1+&quot;&YM2=&quot;+ym2);
		//window.showModalDialog(&quot;Layout.sp?id=fplszjkmx&DJXH=&quot;+nsrdjxh+&quot;&YM1=&quot;+ym1+&quot;&YM2=&quot;+ym2,window,&quot;scroll:no;status:no;dialogWidth:1024px;dialogHeight:800px&quot;);

	}




}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if (sheet == 0 && row == 0 && col == 0) {
		for (var r=1;r&lt;_this.GetRowCount(0);r++) {
			_this.SetCellText(0,r,0,newvalue);
		}
	}
}


//新增
function newRow() 
{
	var ret = window.showModalDialog(&quot;show.sp?grdid=FXDZITEM&guid=&quot;,window,&quot;scroll:no;status:no;dialogWidth:600px;dialoHeight:300px&quot;);
	if (ret == &quot;ok&quot;) {
		loadData();
	}
}

//删除
function deleteRow()
{
	if (!confirm(&quot;删除选中记录，是否继续？&quot;)) return;

	var param = new Object();
	var xmlstr = &quot;&lt;?xml version = &apos;1.0&apos;?&gt;\n&lt;ROWSET&gt;\n&quot;;
	var cnt = 0;
	for (var r=_this.GetMainCellRangeRow1(0);r&lt;=_this.GetMainCellRangeRow2(0);r++) {
		var flag = _this.GetCellText(0,r,0);
		if (flag == &quot;1&quot;) {
			cnt ++;
			xmlstr += &quot;&lt;ROW&gt;&lt;GUID&gt;&quot;+_this.GetCellText(0,r,1)+&quot;&lt;/GUID&gt;&lt;/ROW&gt;\n&quot;;
		}
	}
	xmlstr += &quot;&lt;/ROWSET&gt;&quot;;

	if (cnt == 0) {
		alert(&quot;没有选中记录！&quot;);
		return;
	}

	try {
		param.xmlstr = xmlstr;
		var ret = _this.invokeOSFuncExt(&quot;Delete&quot;,param);
		alert(ret);
		loadData();
	}
	catch (e) {
		alert(&quot;删除失败:&quot;+e.toString());
	}

}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

//删除表信息
function Delete()
{
	var db = null;
	try {
		db = new pub.EADatabase();

		var ds = new pub.EAXmlDS(xmlstr);
		for (var i=0;i&lt;ds.getRowCount();i++) {
			var guid = ds.getStringAt(i,&quot;GUID&quot;);
			var sql = &quot;delete from fxjk_item where guid=&apos;&quot;+guid+&quot;&apos;&quot;;
			db.ExcecutSQL(sql);
		}

		db.Commit();
		return &quot;删除成功！记录数&quot;+ds.getRowCount();
	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}

