union all
select a.xfsbh,b.kpyf,b.hwmc,b.dw,nvl(b.je,0) je,nvl(dj,0) dj from DZDZ_FPXX_ZZSFP a,DZDZ_HWXX_ZZSFP b where a.fpdm=b.fpdm and a.fphm=b.fphm --and a.kpyf&gt;=replace(&apos;[%YM1]&apos;,&apos;-&apos;,&apos;&apos;) --and a.kpyf&lt;=replace(&apos;[%YM2]&apos;,&apos;-&apos;,&apos;&apos;) and to_char(a.kprq,&apos;yyyy-mm-dd&apos;)=&apos;[%DAT1]&apos; ) group by xfsbh ) kp, ( select xfsbh,sum(je) dkje,avg(dj) dkdj from ( select a.xfsbh,b.kpyf,b.hwmc,b.dw,nvl(b.je,0) je,nvl(dj,0) dj from DZDZ_FPXX_PTFP a,DZDZ_HWXX_PTFP b,dzdz.dzdz_fpxx_rzdkl@dzdz rz where a.fpdm=b.fpdm and a.fphm=b.fphm and a.fpdm=rz.fpdm and a.fphm=rz.fphm --and a.kpyf&gt;=replace(&apos;[%YM1]&apos;,&apos;-&apos;,&apos;&apos;) --and a.kpyf&lt;=replace(&apos;[%YM2]&apos;,&apos;-&apos;,&apos;&apos;) and to_char(a.kprq,&apos;yyyy-mm-dd&apos;)=&apos;[%DAT1]&apos;
union all
select a.xfsbh,b.kpyf,b.hwmc,b.dw,nvl(b.je,0) je,nvl(dj,0) dj from DZDZ_FPXX_ZZSFP a,DZDZ_HWXX_ZZSFP b,dzdz.dzdz_fpxx_rzdkl@dzdz rz where a.fpdm=b.fpdm and a.fphm=b.fphm and a.fpdm=rz.fpdm and a.fphm=rz.fphm --and a.kpyf&gt;=replace(&apos;[%YM1]&apos;,&apos;-&apos;,&apos;&apos;) --and a.kpyf&lt;=replace(&apos;[%YM2]&apos;,&apos;-&apos;,&apos;&apos;) and to_char(a.kprq,&apos;yyyy-mm-dd&apos;)=&apos;[%DAT1]&apos; ) group by xfsbh ) dk where dj.nsrsbh=kp.xfsbh(+) and dj.nsrsbh=dk.xfsbh(+) and kp.kpje&gt;[%KPZJE]*10000 order by dj.djxh ) t
