function getSelectedRow()
{
	for (var r=1;r&lt;_this.GetRowCount(0);r++) {
		if (_this.GetCellText(0,r,0) == &quot;1&quot;) {
			//guid  	= _this.GetCellText(0,r,1);
			//kfqy   	= _this.GetCellText(0,r,2);
