//删除
function deleRow()
{
	var r = getSelectedRow();
	if (r == -1) {
		alert(&quot;没有选中记录！&quot;);
		return;
	}
	if (!confirm(&quot;是否确定删除该记录？&quot;)) return;
	var msg = invokeOSFunc(&quot;del&quot;,&quot;guid=&quot;+_this.GetCellText(0,r,1));
	if (msg &gt; 0) {
		alert(&quot;操作成功！&quot;);
		_this.DeleteRow(0,r);
	}
	else {
		alert(&quot;操作失败！&quot;+msg);
	}
}

//修改
function modify()
{
	var row = getSelectedRow();
	guid=_this.GetCellText(0,row,1);
	var url = &quot;show.sp?grdid=JIAXCSWH_LR&guid=&quot;+guid+&quot;&act=update&quot;;
	//var url = &quot;show.sp?grdid=CMEDITHZ&guid=&quot;+gid+&quot;&cmid=&quot;+cmid+&quot;&name=&quot;+name+&quot;&ammno=&quot;+ammno+&quot;&typ=&quot;+typ+&quot;&act=update&quot;;
	//var url = &quot;show.sp?grdid=FDCCSWHB_LR&act=add&quot;;
	ret = window.showModalDialog(url,window,&quot;scroll:no;status:no;dialogWidth:500px;dialoHeight:100px&quot;);
}



//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	if (sheet == 0 && row &gt;= _this.GetMainCellRangeRow1(0) && row &lt;= _this.GetMainCellRangeRow2(0)) {
	//var row = getSelectedRow();
	guid=_this.GetCellText(0,row,1);
	var url = &quot;show.sp?grdid=JIAXCSWH_LR&guid=&quot;+guid+&quot;&act=update&quot;;
	//var url = &quot;show.sp?grdid=CMEDITHZ&guid=&quot;+gid+&quot;&cmid=&quot;+cmid+&quot;&name=&quot;+name+&quot;&ammno=&quot;+ammno+&quot;&typ=&quot;+typ+&quot;&act=update&quot;;
	//var url = &quot;show.sp?grdid=FDCCSWHB_LR&act=add&quot;;
	ret = window.showModalDialog(url,window,&quot;scroll:no;status:no;dialogWidth:500px;dialoHeight:100px&quot;);
	}

}

ROW.GRDOSDS_VALUE:
var pubpack = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

function del()
{
	try {
		var sql = &quot;delete from TAX_JIAXSFCSB where jiaxsfcsuuid=&apos;&quot;+guid+&quot;&apos;&quot;;
		var ret = pubpack.EADbTool.ExcecutSQL(sql);
		return ret;
	}
	catch(e) {
		return e.toString();
	}
}

ROW.DATDSC:
select &apos;&apos;lx,jiaxsfcsuuid,sfcsdm,diqlx,jiazlx,lingqlx,baomf,
to_char(yxqq,&apos;yyyy-mm-dd&apos;)yxqq,to_char(yxqz,&apos;yyyy-mm-dd&apos;)
from TAX_JIAXSFCSB
where diqlx like &apos;%[%DIQLX]%&apos;
and jiazlx like &apos;%[%JIAZLX]%&apos;
and lingqlx like &apos;%[%LINGQLX]%&apos;
