//数据加载完毕
function _thisOnpost_loaddata(sheet)
{

	_this.SplitSheet(1,&quot;V&quot;,&quot;80&quot;);

	var hyList = _this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_TAX_DM_HY&where=mlbz=&apos;Y&apos;&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	_this.SetToComboBox(&quot;&quot;,1,1,2,hyList);
	//税务机关
	var swjgList = _this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_SWJG&where=sjid=&quot;+G_DEPTID+&quot; or id=&quot;+G_DEPTID),&quot;ID&quot;,&quot;NAME&quot;);
	_this.SetToComboBox(&quot;&quot;,1,1,6,swjgList);
	_this.SetCellText(1,1,6,G_DEPTID);

	var yesnoList = _this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_YESNO&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	_this.SetToComboBox(&quot;&quot;,1,1,4,yesnoList);
	var nsrztList = _this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_TAX_NSR_ZT2&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	_this.SetToComboBox(&quot;&quot;,1,3,2,nsrztList);


	_this.AutoFixScreenWidth();
	ZXGFILE0 = _this.SaveTempZXGFile(0);

	//setTimeout(&quot;loadData()&quot;,100);

}

//等待时执行的任务
function runWaitingTask()
{
	var hy = _this.GetCellText(1,1,2);
	var smqy = _this.GetCellText(1,1,4);
	var zgswjg = _this.GetCellText(1,1,6);	
	var nsrzt = _this.GetCellText(1,3,2);	
	var swgly = _this.GetCellText(1,3,4);
	var nsrmc = _this.GetCellText(1,3,6);	
	var xml =  _sql.query(&quot;MAIN&quot;,&quot;HY=&quot;+hy+&quot;&NSRZT=&quot;+nsrzt+&quot;&SMQY=&quot;+smqy+&quot;&ZGSWJG=&quot;+zgswjg+&quot;&SWGLY=&quot;+swgly+&quot;&NSRMC=&quot;+nsrmc);
	return xml;
}

function loadData()
{	
	_this.LoadZXGFile(ZXGFILE0,-1,0);
	_this.SetFixedRow(0,3);
	_this.SetFixedCol(0,1);
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this.SELECTFLAG_SINGLEROW);	//设置鼠标选择单行

	_this.SetCellText(0,3,2,&quot;正在查询...&quot;);
	//var xml = _sql.query(&quot;MAIN&quot;,&quot;&quot;);
	var xml = showModalDialog(&quot;show.sp?grdid=Waiting&quot;,window,&quot;scroll:no;status:no;dialogWidth=200px;dialogHeight=80px&quot;);
	_this.SetCellText(0,3,2,&quot;&quot;);
	_this.SetMainCellRange(0,3,1,9,31);
	_this.SetXmlDS(0,3,1,9,31,xml);
	_this.SetToBoolBox(0,2,0);
	for (var r=3;r&lt;_this.GetRowCount(0);r++) {
		if (_this.GetCellText(0,r,1) != &quot;&quot;) {
			_this.SetToBoolBox(0,r,0);
		}
	}

	_sql.querytods(&quot;LASTUPDATE&quot;,&quot;&quot;);
	_this.SetCellText(0,1,3,_this.XMLDS_GetString(0,&quot;UPTIME&quot;));

	_this.AutoFixScreenWidth();

}

//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if (id == &quot;查询&quot;) {
		loadData();
	}
}


