//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
  	//loaddoc();
//  	_this.SetColVisible(sheet,7,-1);
	_this.AutoFixScreenWidth();

	if (_this.GetCellText(sheet,12,2) != &quot;&quot;) {
		_this.SetCellText(0,12,7,&quot;【双击下载】&quot;);
	}

	if (stat == 13 ) {
		var chkflg1 = _this.GetCellText(sheet,1,2);
		if (chkflg1 == &quot;1&quot;) {
			alert(&quot;电力税负异常检查完毕，没有异常！&quot;);
		}
		else if (chkflg1 == &quot;2&quot;) {
			alert(&quot;电力税负异常检查完毕，仍然有异常！&quot;);
		}
	}

	var tmp = _this.GetCellText(0,2,6);
	if(tmp == &quot;6&quot;)
	{
		_this.SetCellText(0,4,1,&quot;电表编号：&quot;);
	}

	var chkrol = invokeOSFunc(&quot;checkGenTask&quot;,&quot;usrid=&quot;+G_USRID+&quot;&thisorgid=&quot;+G_ORGID);
	if (chkrol &gt; 0) {
		_this.AddToolbarButton(&quot;udf_btnback&quot;,&quot;返回首页&quot;,&quot;&quot;,&quot;&quot;,0,0,0,100);

	}

}
