ROW.GRDJSDS_VALUE:
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SetToFileSelectBox(&quot;&quot;,sheet,3,3); //设置单元格为一个选择文件的输入框
	_this.ShowTabBar(1);
}

//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
   	if(id == &quot;导入&quot;) {
		if(!confirm(&quot;确定导入文件？&quot;)) return ;

		var filepath = &quot;&quot;;
		filepath =_this.GetCellText(sheet,3,3); //获取导入文件路径
		//alert(&quot;filepath&quot;+filepath);
		if (filepath != &quot;&quot;) { 
		           _this.SetCellText(sheet,3,3,&quot;正在导入数据...&quot;+filepath);
		           runImpl(filepath);
                }
                _this.SetCellText(sheet,3,3,&quot;&quot;);
	}
}

function runImpl(filepath)
{
	try {
		var tabnam =  runImp(filepath);
		//alert(&quot;tabnam&quot;+tabnam);
		if(tabnam != null && tabnam != &quot;&quot;) {
			var param = new Object();
			param.tabnam = tabnam;
			var msg = invokeOSFuncExt(&quot;importData&quot;,param);
			_this.SetCellText(0,6,2,msg);
			alert(msg);			
		}
	}
	catch(e) {
		alert(&quot;导入失败！&quot;+e);
	}
}

ROW.GRDOSDS_VALUE:
var pubpack = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);
var xlsdb = new JavaPackage ( &quot;com.xlsgrid.net.xlsdb&quot; );

//GDP数据导入
function importData()
{
	var db = null;
	var ds = null;
	var sql = &quot;&quot;;
	var tabname = tabnam;
	//var typ = typ;
	var ret = &quot;&quot;;
	var updcount = 0;
	var inscount = 0;
	try{
		db = new pubpack.EADatabase();

		sql = &quot;select * from user_tables where table_name=upper(&apos;&quot;+tabname+&quot;&apos;)&quot;;
		var cnt = db.GetSQLRowCount(sql);
		if(cnt &lt;= 0) return &quot;无数据可以导入&quot;;

		var mycnt = 0;

		//修改表，增加列名，存储数据所属月份
		sql = &quot;alter table &quot;+tabname+&quot; add yymm varchar2(10)&quot;;		
		db.ExcecutSQL(sql);	

		sql = &quot;update &quot;+tabname+&quot; set co0=replace(co0,&apos;？&apos;,&apos;&apos;)&quot;;
		db.ExcecutSQL(sql);

		sql = &quot;update &quot;+tabname+&quot; set yymm=(
			select to_char(to_date(substr(co0,0,4) ||substr(co0,instr(co0,&apos;-&apos;),instr(co0,&apos;月&apos;)-instr(co0,&apos;-&apos;)),&apos;yyyy-mm&apos;),&apos;yyyymm&apos;) yymm 
			from &quot;+tabname+&quot; where co0 like &apos;%国民经济主要指标%&apos;
			)&quot;;
		db.ExcecutSQL(sql);

		sql = &quot;insert into TAX_TJJDATA(zbbh,zbmc,ljzl,Zsbl,yymm)
			select rownum zbbh,co0,replace(co1,&apos;―&apos;,&apos;&apos;) ljzl,co2,yymm
			from &quot;+tabname+&quot; 
			where co0 not like &apos;%国民经济主要指标%&apos; 
			  and co0 not like &apos;指标%&apos; and co1 not like &apos;总量（万元）&apos;
			  and yymm not in (select yymm from TAX_TJJDATA)&quot;;

		inscount = db.ExcecutSQL(sql);	

		ret = &quot;导入完成！共新增&quot;+inscount+&quot;条记录&quot;;

		//删除临时表
		sql = &quot;drop table &quot;+tabname;
		db.ExcecutSQL(sql);			

		db.Commit();

		return ret;
	}
	catch(e) {
		if(db != null) db.Rollback();
		return e.toString();
	}

	finally {
		if(db != null)	db.Close();
	}
}
