//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.ShowToolBar(0);
	document.title = &quot;正在处理,请稍候...&quot;;	
	setTimeout(&quot;callParentFunction()&quot;,100);

}

//调用父窗口的任务方法
function callParentFunction()
{
	var ret = &quot;&quot;;
	var pWindow = window.dialogArguments;
	if (pWindow != null) {
		ret = pWindow.runWaitingTask();
	}
	else {
		//window.opener.runWaitingTask();
	}

	window.returnValue = ret; 
	window.close(); 

}

