//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_sql.querytods(&quot;TJDS&quot;,&quot;&quot;);
	var gxfz = _this.XMLDS_GetString(0,&quot;GX_FZ&quot;);
	var gxfm = _this.XMLDS_GetString(0,&quot;GX_FM&quot;);
	var gxbl = _this.XMLDS_GetString(0,&quot;GX_BL&quot;);
	var hzfz = _this.XMLDS_GetString(0,&quot;HZ_FZ&quot;);
	var hzfm = _this.XMLDS_GetString(0,&quot;HZ_FM&quot;);
	var hzbl = _this.XMLDS_GetString(0,&quot;HZ_BL&quot;);

	//var gxstr = &quot;广西&quot;+formatStr(&quot;&quot;,6)+formatStr(gxfz,10)+formatStr(gxfm,12)+formatStr(gxbl,10);
	var gxstr = &quot;广西&quot;+formatStr(&quot;&quot;,6)+formatStr(&quot;--&quot;,10)+formatStr(&quot;--&quot;,12)+formatStr(&quot;--&quot;,10);
	var hzstr = &quot;贺州&quot;+formatStr(&quot;&quot;,6)+formatStr(hzfz,10)+formatStr(hzfm,12)+formatStr(hzbl,10);
	_this.SetCellText(0,3,7,gxstr);
	_this.SetCellText(0,4,7,hzstr);

	_this.SetCellText(0,4,3,G_LOGDAT);

	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this.SELECTFLAG_SINGLEROW);
	_this.AutoFixScreenWidth();
}

function formatStr(str,len)
{
	var spaceStr = &quot;&quot;;
	var strlen = str.length;
	if (len &gt; strlen) {
		for (var i=0;i&lt;len-strlen;i++) {
			spaceStr += &quot; &quot;; //全角的空格
		}
		return spaceStr + str;
	}

	return str;
}
