//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if (sheet == 0 && row == 0 && col == 0) {
		for (var r=1;r&lt;_this.GetRowCount(0);r++) {
			_this.SetCellText(0,r,0,newvalue);
		}
	}
}


//新增
function newRow() 
{
	var ret = window.showModalDialog(&quot;show.sp?grdid=FXDZITEM&guid=&quot;,window,&quot;scroll:no;status:no;dialogWidth:600px;dialoHeight:300px&quot;);
	if (ret == &quot;ok&quot;) {
		loadData();
	}
}

//删除
function deleteRow()
{
	if (!confirm(&quot;删除选中记录，是否继续？&quot;)) return;

	var param = new Object();
	var xmlstr = &quot;&lt;?xml version = &apos;1.0&apos;?&gt;\n&lt;ROWSET&gt;\n&quot;;
	var cnt = 0;
	for (var r=_this.GetMainCellRangeRow1(0);r&lt;=_this.GetMainCellRangeRow2(0);r++) {
		var flag = _this.GetCellText(0,r,0);
		if (flag == &quot;1&quot;) {
			cnt ++;
			xmlstr += &quot;&lt;ROW&gt;&lt;GUID&gt;&quot;+_this.GetCellText(0,r,1)+&quot;&lt;/GUID&gt;&lt;/ROW&gt;\n&quot;;
		}
	}
	xmlstr += &quot;&lt;/ROWSET&gt;&quot;;

	if (cnt == 0) {
		alert(&quot;没有选中记录！&quot;);
		return;
	}

	try {
		param.xmlstr = xmlstr;
		var ret = _this.invokeOSFuncExt(&quot;Delete&quot;,param);
		alert(ret);
		loadData();
	}
	catch (e) {
		alert(&quot;删除失败:&quot;+e.toString());
	}

}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

//删除表信息
function Delete()
{
	var db = null;
	try {
		db = new pub.EADatabase();

		var ds = new pub.EAXmlDS(xmlstr);
		for (var i=0;i&lt;ds.getRowCount();i++) {
			var guid = ds.getStringAt(i,&quot;GUID&quot;);
			var sql = &quot;delete from fxjk_item where guid=&apos;&quot;+guid+&quot;&apos;&quot;;
			db.ExcecutSQL(sql);
		}

		db.Commit();
		return &quot;删除成功！记录数&quot;+ds.getRowCount();
	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}

ROW.DATDSC:
select rownum,t.* from (
select djxh,nsrsbh,shxydm,nsrmc,
  (select nsrztmc from  DM_GY_NSRZT zt where a.nsrzt_dm=zt.nsrzt_dm) nsrzt, 
  (select djzclxmc from DM_DJ_DJZCLX zclx where a.djzclx_dm=zclx.djzclx_dm) djzclx,
  (select hymc from DM_GY_HY hy where a.hy_dm=hy.hy_dm and hy.yxbz=&apos;Y&apos;)hy,
  (select swryxm from DM_GY_SWRY swry where swry.swry_dm=a.SSGLY_DM)ssgly,
  (select name from v_swjg sw where sw.id=substr(a.zgswskfj_dm,1,7))zgswjg,
  (select swjgmc from DM_GY_SWJG jg where a.zgswskfj_dm=jg.swjg_dm)zgswskfj,
  (select lbmc from DJ_DJHZDYLBSZ  lb where trim(a.lb_dm)=trim(lb.lb_dm)) lb
from TAX_ZDSY_QHJT a
where a.zgswskfj_dm like &apos;[%ZGSWJG]%&apos;
  and nsrsbh like &apos;%[%NSRSBH]%&apos;
  and nsrmc like &apos;%[%NSRMC]%&apos;
  and nsrzt_dm like &apos;[%NSRZT]%&apos;
  and lb_dm like &apos;[%ZDYLB]%&apos;
  and ssgly_dm like &apos;[%SSGLY]%&apos;
order by djxh
) t

select rownum,t.* from (
select a.djxh,a.nsrsbh,a.nsrmc,
  (select nsrztmc from  DM_GY_NSRZT zt where b.nsrzt_dm=zt.nsrzt_dm) nsrzt, 
  dqylxmc,
  (select djzclxmc from DM_DJ_DJZCLX zclx where b.djzclx_dm=zclx.djzclx_dm) djzclx,
  (select hymc from DM_GY_HY hy where b.hy_dm=hy.hy_dm and hy.yxbz=&apos;Y&apos;)hy,
  b.shxydm,
  (select swryxm from DM_GY_SWRY swry where swry.swry_dm=b.SSGLY_DM)ssgly,
  (select name from v_swjg sw where sw.id=substr(b.zgswskfj_dm,1,7))zgswjg,
  (select swjgmc from DM_GY_SWJG jg where b.zgswskfj_dm=jg.swjg_dm)zgswskfj
from TAX_ZDSY_DQY a , (
select djxh,nvl(shxydm,nsrsbh)nsrsbh,shxydm,NSRMC,djrq,jdxz_dm,YXBZ,NSRZT_DM,zgswskfj_dm,hy_dm,djzclx_dm,ssgly_dm
 from dj_nsrxx 
 where zgswskfj_dm like &apos;14511%&apos;
 and yxbz=&apos;Y&apos;) b
where a.djxh=b.djxh(+)
  and ( substr(b.zgswskfj_dm,1,7) in (&apos;[%ZGSWJG]&apos;)
     or substr(b.zgswskfj_dm,1,7) in (select swjg.id from V_SWJG swjg 
           start with swjg.id=&apos;[%ZGSWJG]&apos; connect by prior swjg.id=swjg.sjid))
  and a.nsrsbh like &apos;%[%NSRSBH]%&apos;
  and a.nsrmc like &apos;%[%NSRMC]%&apos;
  and b.nsrzt_dm like &apos;[%NSRZT]%&apos;
  and a.dqylx_dm like &apos;[%DQYLX]%&apos;
  and b.ssgly_dm like &apos;[%SSGLY]%&apos;
order by djxh
) t
